import type { QuoteCard } from "@/types/content";

/** Citações dos Padres do Deserto (Apotegmas). */
export const DESERT_FATHERS_QUOTES: Omit<QuoteCard, "id">[] = [
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem senta no deserto e guarda o silêncio escapa a três guerras: a do ouvido, a da língua e a do coração." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A natureza do homem é fraca; é preciso orar para não cair na tentação." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Vai sentar-te na tua cela e a tua cela te ensinará tudo." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Arsenius, foge dos homens e serás salvo." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se quiseres ser monge, sê como o morto: não te indignes quando és ultrajado, não te ensoberbeças quando és louvado." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Nunca me deitei tendo algo contra alguém, e nunca deixei alguém deitar-se tendo algo contra mim." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Pela graça de Deus, desde que saí para o deserto, não proferi palavra de que me arrependa." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Sede como o publicano; humilhai o vosso espírito e sereis exaltados." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não tenta não progride; não há virtude sem tentação." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem que guarda rancor ao orar é como quem semeia no mar." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Eu sou como uma árvore plantada à beira da água; dou fruto na estação; minha folha não murcha." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como os querubins: todo olho." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração é o diálogo da mente com Deus." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não é o jejum que importa, mas a humildade; o que ganhas pelo jejum, perdes pela soberba." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência é a primeira das virtudes; por ela se expulsam os demônios e se abrem as portas do céu." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem habita no deserto e na solidão está livre de três guerras: a do ouvido, a da língua e a dos olhos." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não dês ao teu coração o que fere os teus olhos." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se te repreenderem, não te justifiques; a justificação remove a penitência." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o teu coração com toda a diligência, porque dele procedem as fontes da vida." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não desprezes ninguém; quem despreza o outro peca." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "O tempo que passas sem oração é tempo perdido." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A tribulação que vem dos homens é melhor que a tranquilidade que vem da nossa vontade." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "O que não é útil para a alma, não o faças." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Quem edifica sobre a obediência edifica sobre a rocha." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Cada um cumpre o mandamento segundo a sua capacidade; Deus não rejeita a oferta." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas onde dorme a raiva; reconcilia-te antes do pôr do sol." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "Passei quatorze anos na Sceté suplicando a Deus noite e dia para me dar força de dominar a ira." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Se o homem não disser no seu coração: só eu e Deus existimos no mundo, não terá descanso." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não pode deixar os pais e os bens e renunciar a si mesmo não pode ser meu discípulo." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem deve dar a cada um o que lhe é devido: ao corpo, o trabalho; à alma, a oração; ao próximo, a caridade." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O pai que é duro consigo e misericordioso com os outros é um verdadeiro monge." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se te acusares, Deus te defenderá; se te justificares, Deus te acusará." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te deites com rancor no coração; vai e reconcilia-te, e depois volta." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "Não há outro caminho senão este: humilhar-se e dizer: perdoai-me." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem permanece na solidão e no silêncio escapa a três tentações: a do ouvido, a da língua e a da vista." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A soberba e a vanglória anulam todo o bem; a humildade apaga todo o mal." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "O monge é quem está separado de todos e unido a todos." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "O que é acima da tua força não o faças; guarda o que podes e serás salvo." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Não julgar ninguém, acolher a todos e rezar por todos: eis o caminho." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Nunca dormi tranquilo tendo contra alguém uma má palavra; antes ia reconciliar-me e depois descansava." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Se queres ser perfeito, vai vender o que tens, dá aos pobres e segue-me." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Faze o que puderes; Deus não exige o que está acima das tuas forças." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando quiseres edificar, começa por ti; não critiques os outros." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não fales mal de ninguém; quem fala mal do irmão peca gravemente." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem habita no deserto em silêncio está livre da maledicência, da vanglória e da curiosidade." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Arsenius, trabalha e não fales; o silêncio conduz à paz." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "O que sai da boca do homem é o que o contamina; guarda a tua língua e guardarás a tua alma." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Ameaça os teus pensamentos; não os deixes vaguear; traz-nos de volta à cela." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não há virtude maior que não julgar o próximo." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A tribulação é necessária; sem ela não entramos no reino dos céus." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Eu queria encontrar um homem e inclinar-me diante dele; nisso coloquei toda a minha esperança." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve rezar sem cessar; a oração contínua purifica o coração." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te aflijas por nada; confia em Deus e Ele te sustentará." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Reza em segredo; o Pai que vê no segredo te recompensará." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "A vida e a morte dependem do próximo; quem ganha o irmão ganha a Deus." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem que guarda a memória das injúrias é como quem bebe veneno esperando que o outro morra." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Nunca me deitei sem primeiro perdoar a quem me ofendeu." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A língua do monge está no seu coração; o que não sai do coração não sai da boca." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração é a supressão dos pensamentos." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Senta-te na tua cela e ela te ensinará a sabedoria; fuge e serás salvo." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser considerado alguém; esconde as tuas virtudes e serás em paz." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com alegria é a mãe de todas as virtudes." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Dá ao corpo o que precisa, não o que quer; e a alma crescerá." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não humilha o coração não pode rezar; a oração nasce da humildade." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te entregues à tristeza; a tristeza do mundo produz a morte; a tristeza segundo Deus produz a conversão." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "O que não edifica o próximo não o digas." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não busques ser honrado; busca ser humilde e Deus te exaltará." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A porta de Deus está sempre aberta; quem bate, entra." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Arsenius, fuge, cala-te e descansa; estas são as raízes da impecabilidade." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Ama o trabalho e a pobreza; deles nasce a liberdade do coração." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não perguntes sobre os pecados dos outros; pergunta sobre os teus." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te desculpes; quem se desculpa acusa-se a si mesmo." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem reza verdadeiramente não se lembra nem do corpo nem do que está fora; só de Deus." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve vigiar; a vigília afasta os maus pensamentos." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Não fales; a palavra é de prata, o silêncio é de ouro." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Cada um dá o que pode; Deus aceita a oferta conforme o coração." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Eu nunca dormi com algo contra o meu irmão; antes ia e pedia perdão." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não critiques ninguém; quem critica o irmão perde a paz." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A soberba precede a queda; a humildade precede a glória." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é aquela que não imagina nenhuma forma." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te aflijas pelo amanhã; o amanhã terá as suas aflições." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem reza de noite afasta as tentações do dia; quem negligencia a oração é vencido." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Quem se humilha será exaltado; quem se exalta será humilhado." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças ao outro o que não queres que te façam." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "O que está acima das tuas forças não o carregues; cumpre o que podes." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem que guarda a boca guarda a alma; quem fala demais não tem paz." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Muitas vezes me arrependi de ter falado; nunca me arrependi de ter calado." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se queres salvar a alma, considera-te pecador e submete-te a todos." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glorifies; a glória do homem é passageira." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como fogo; quem a guarda não peca." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia e ora cai na tentação; o espírito está pronto, a carne é fraca." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A compunção e as lágrimas purificam a alma; não as desprezes." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração; dele saem os bons e os maus pensamentos." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser pobre de espírito; os pobres de espírito possuem o reino dos céus." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "Não posso dominar-te, mas Deus pode; entrego-te a Ele." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Senta-te na tua cela; ora em segredo; o Pai te recompensará." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia constantemente cai nas armadilhas do demônio." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A palavra do ancião tem poder; guarda-a no coração." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não te preocupes com o que os outros fazem; preocupa-te contigo mesmo." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a mãe da oração; a oração é a mãe da contemplação." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Quem se considera nada alcança tudo; quem se considera tudo não alcança nada." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro a obediência à oração; a obediência é a maior virtude." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Não julgues o teu irmão; julga-te a ti mesmo e encontrarás paz." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "A cela do monge é o seu refúgio; quem a abandona perde a paz." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Quem se humilha diante de todos será exaltado por Deus." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser vigilante; a negligência traz a ruína." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração sem distrações é a meta do monge." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Esconde as tuas virtudes como escondes os teus pecados." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem murmuração agrada a Deus." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda os teus pensamentos; eles determinam a tua vida." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada por vanglória; tudo faça por amor a Deus." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Cada um serve conforme a sua capacidade; Deus vê o coração." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva é como fogo; apaga-a antes que queime tudo." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência vence tudo; quem tem paciência tem tudo." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de mais nada." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge é aquele que se esquece de si mesmo." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e deixa o resto para Deus." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a porta do céu; quem passa por ela entra." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia a tudo não pode seguir a Cristo." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o que não podes mudar; aceita e reza." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa a si mesmo não precisa de acusadores." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho manual acompanha a oração; ambos são necessários." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares com ninguém; compara-te apenas com Deus." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser corrigido a corrigir; a correção humilha e salva." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como água viva; quem bebe não tem sede." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra Deus; quem busca o mundo perde Deus." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é melhor que o sacrifício; obedece e serás salvo." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como o sal; preserva e dá sabor." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura não conhece imagens; é pura contemplação." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser conhecido; esconde-te e Deus te conhecerá." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência alegre é mais valiosa que o jejum triste." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os teus pensamentos; eles são a raiz das ações." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem propósito; tudo deve servir a Deus." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus não olha para a quantidade, mas para o coração." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A reconciliação é melhor que a oferta; reconcilia-te primeiro." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A ira destrói a paz; quem domina a ira domina tudo." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não teme nada; Deus é suficiente." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro é aquele que não se vê a si mesmo." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses com o que não podes fazer; faz o que podes." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade abre todas as portas; a soberba fecha todas." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia cai; quem vigia permanece de pé." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes com o amanhã; vive o hoje em Deus." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será exaltado; quem se exalta será humilhado." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é ouro; a palavra é prata; usa-as com sabedoria." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories em nada; tudo vem de Deus." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser vencido a vencer; a derrota humilha e salva." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como luz; ilumina o caminho." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita no céu; não a abandones." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a chave do céu; obedece e entrarás." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a luz; brilha sem se mostrar." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira não tem palavras; é puro silêncio." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques ser visto; esconde-te e Deus te verá." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com amor é perfeita; obedece com alegria." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração; dele depende toda a vida." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada por ti mesmo; tudo faça por Deus." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus aceita o que ofereces com amor; o amor santifica tudo." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas com rancor; reconcilia-te antes de dormir." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é a mãe de todas as virtudes; cultiva-a." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de testemunhas." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge é invisível; não se mostra." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e descansa em Deus." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a rainha das virtudes; cultiva-a sempre." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode possuir; renuncia e possuirás." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o passado; vive o presente em Deus." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não precisa de juízes; acusa-te e serás justo." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho e a oração caminham juntos; não os separes." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares; cada um tem o seu caminho." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser humilhado a ser honrado; a humilhação salva." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como pão; alimenta a alma." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra paz; quem busca o mundo encontra guerra." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é o caminho; obedece e chegarás ao fim." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como o vento; passa sem deixar rastro." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é sem pensamentos; é pura atenção." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser famoso; esconde-te e serás conhecido por Deus." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem questionar é perfeita; obedece sem duvidar." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os pensamentos; eles determinam o destino." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem amor; o amor santifica tudo." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus vê o coração; oferece com amor e será aceito." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva é veneno; apaga-a antes que te mate." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência vence todas as dificuldades; tem paciência." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não teme a solidão." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro não existe; só existe Deus nele." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses; faz o que podes e confia em Deus." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a base; edifica sobre ela." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia não vê; quem vigia vê tudo." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes; confia em Deus e Ele cuidará de ti." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será levantado; quem se levanta será derrubado." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a melhor oração; cala-te e ouvirás Deus." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories; tudo é dom de Deus." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser corrigido a estar certo; a correção ensina." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como remédio; cura a alma." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita no paraíso; não a deixes." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a porta; obedece e passarás." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como o sol; brilha sem se exibir." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira é sem imagens; é pura presença." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques glória; esconde-te e serás glorificado por Deus." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com fé é poderosa; obedece com confiança." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração como guardas um tesouro; é precioso." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem propósito divino; tudo deve glorificar Deus." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus não olha para o tamanho, mas para o amor." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas sem paz; reconcilia-te e dormirás bem." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é ouro; cultiva-a e serás rico." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de explicações." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge desaparece; só Deus permanece." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e deixa o resto." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a coroa; usa-a sempre." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode receber; renuncia e receberás." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas; Deus está contigo sempre." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não tem inimigos; acusa-te e terás paz." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho manual é oração; trabalha e ora ao mesmo tempo." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares com os outros; compara-te com Cristo." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser vencido a vencer; a derrota é vitória." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como mel; adoça a alma." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra descanso; quem busca o mundo encontra cansaço." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a escada; obedece e subirás." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a água; flui sem resistência." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é sem palavras; é puro ser." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser admirado; esconde-te e serás amado por Deus." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem hesitação é perfeita; obedece imediatamente." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os pensamentos; eles são sementes de ações." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada por interesse; tudo faça por amor." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus aceita o que ofereces com humildade; a humildade santifica." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva queima; apaga-a com o perdão." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é força; quem tem paciência é forte." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de consolo humano." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro não se vê; só vê Deus." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses demais; descansa e trabalha com moderação." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a chave; abre todas as portas." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia não sabe; quem vigia sabe tudo." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes com o futuro; vive o presente." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será honrado; quem se honra será humilhado." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a melhor resposta; cala-te e ouvirás." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories em ti mesmo; gloria-te em Deus." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser humilhado a ser exaltado; a humilhação eleva." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como semente; planta-a no coração." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita na presença de Deus." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a estrada; obedece e caminharás." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a terra; recebe tudo sem reclamar." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira é sem formas; é pura essência." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques honra; esconde-te e serás honrado por Deus." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com gratidão é perfeita; obedece agradecendo." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração como guardas a vida; é precioso demais." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem devoção; tudo faça com amor." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus vê a intenção; oferece com pureza e será aceito." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas com mágoa; perdoa e dormirás em paz." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é sabedoria; quem tem paciência é sábio." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de aprovação." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge não existe para si; existe para Deus." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e confia." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a luz; ilumina o caminho." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode seguir; renuncia e seguirás." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o que não podes controlar; entrega a Deus." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não tem medo; acusa-te e terás coragem." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho é oração quando feito com devoção." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares; cada um tem a sua medida." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser corrigido a estar errado sozinho; a correção ajuda." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como espada; corta o mal." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra liberdade; quem busca o mundo encontra escravidão." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a ponte; obedece e passarás." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a lua; reflete a luz sem possuí-la." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é sem conceitos; é pura realidade." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser elogiado; esconde-te e serás louvado por Deus." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem reclamação é perfeita; obedece em silêncio." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os pensamentos; eles são portas do coração." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem caridade; tudo faça por amor ao próximo." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus não olha para o valor, mas para o coração que oferece." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva separa; o perdão une; escolhe o perdão." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é virtude; cultiva-a e serás virtuoso." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de reconhecimento." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro é transparente; só Deus se vê nele." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses inutilmente; trabalha com sabedoria." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a raiz; dela nasce tudo." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia não entende; quem vigia entende tudo." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes; Deus conhece as tuas necessidades." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será exaltado por Deus; confia nisso." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a melhor pregação; cala-te e ensinarás." },
    { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories em nada terreno; gloria-te em Deus." },
    { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser vencido a vencer injustamente; a justiça importa." },
    { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como bálsamo; cura as feridas." },
    { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita no santuário; respeita-a." },
    { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é o remédio; obedece e serás curado." },
    { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a montanha; firme e silencioso." },
    { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira é sem distrações; é pura atenção a Deus." },
    { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques ser importante; esconde-te e serás importante para Deus." },
    { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com prontidão é perfeita; obedece sem demora." },
    { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração como guardas os olhos; é sensível." },
    { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem discernimento; tudo faça com sabedoria." },
    { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus aceita o que ofereces com simplicidade; a simplicidade agrada." },
    { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas com ódio; perdoa e dormirás tranquilo." },
    { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é poder; quem tem paciência é poderoso." },
    { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de validação humana." },
    { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge não se busca; busca só a Deus." },
    { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e deixa Deus fazer o resto." },
    { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a porta; passa por ela e entrarás." },
    { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode ganhar; renuncia e ganharás." },
    { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o que não depende de ti; confia em Deus." },
    { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não tem orgulho; acusa-te e serás humilde." },
    { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho manual é adoração quando feito com fé." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares com ninguém; sê tu mesmo diante de Deus." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser humilhado a humilhar; a humildade é melhor." },
  { category: "patristic", author: "Abba Amun", source: "Apotegmas", text: "Suporta todo homem assim como Deus te suporta." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Conserva sempre a qualidade de estranho: a familiaridade é a matriz de todas as paixões." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "É preciso que o monge não deixe que a consciência o acuse em coisa nenhuma." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Sem a observância dos preceitos divinos, o homem não progride em uma só virtude." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Nunca adormeci tendo alguma coisa contra alguém; nem deixei que alguém fosse dormir tendo algo contra mim." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Ser herege é separar-se de Deus, e eu não me quero separar de Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O trabalho do corpo é a folhagem; a disciplina da alma é o fruto." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Não há labor igual ao da oração: sempre que o homem quer orar, os inimigos tentam arredá-lo." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Passou três anos com uma pedra na boca até adquirir o hábito do silêncio." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Ao vender ou comprar, dizia o preço apenas uma vez e recebia o valor em silêncio." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O dar e o receber eram para mim ágape: o lucro de meu irmão é obra de frutificação." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O homem irascível, ainda que ressuscite um morto, não é agradável a Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Vai, atira diante de Deus a tua fraqueza contra a fornicação, e encontrarás sossego." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Se alguém me fosse extremamente caro e eu soubesse que ele me leva ao pecado, eu o afastaria de mim." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "É preciso que, a toda hora, o homem se recorde do juízo de Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Se me fosse possível encontrar um leproso e dar-lhe o meu corpo em troca do dele, fá-lo-ia com prazer; pois esta é a caridade perfeita." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Sou homem; como hei de saber se meu esforço agradou a Deus? Um é o modo de julgar de Deus, outro o dos homens." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Nutre em teu espírito os pensamentos dos malfeitores no cárcere: quando há de vir o Diretor? Assim o monge deve arguir sua alma: como me apresentarei ante o tribunal de Cristo?" },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Passei catorze anos na Cétia, pedindo a Deus, dia e noite, que me concedesse vencer a ira." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Senta-te em tua cela, come um pouco diariamente, e conserva a palavra do publicano em teu coração. Assim poderás ser salvo." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Com estabilidade caminhemos na via do Senhor, sem vaguear na medida em que se precipitam os pensamentos." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Rogo-te, Senhor meu Deus, não deixes perecer a tua criatura." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "A via estreita e atribulada é esta: fazer violência aos seus pensamentos e, por causa de Deus, cortar a vontade própria." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Senhor, quero ser salvo, e não me deixam os pensamentos; que farei na minha tribulação?" },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Antônio, cuida de ti mesmo, pois estes são juízos de Deus e não te convém penetrá-los." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Em qualquer lugar que vás, tem sempre Deus ante os olhos; qualquer coisa que faças, procura o testemunho das Sagradas Escrituras." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Esta é a grande labuta do homem: assumir sobre si a culpa própria diante de Deus e esperar a tentação até o último suspiro." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Ninguém sem tentação poderá entrar no reino dos céus; se suprimires as tentações, não acharás ninguém salvo." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Não confies em tua justiça, não te arrependas do que já passou, e sê continente de língua e de ventre." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Vi todas as armadilhas do inimigo estendidas sobre a terra. Quem escapará a elas? Ouvi uma voz: A humildade." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Há quem consuma o corpo em ascese e, por não ter discrição, se afaste de Deus." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Do próximo dependem a vida e a morte: se lucramos o irmão, lucramos a Deus; se o escandalizamos, pecamos contra Cristo." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Como os peixes na terra seca morrem, assim os monges fora da cela relaxam. É preciso que nos apressemos para a cela como o peixe para o mar." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem permanece no deserto liberta-se de três combates: o dos ouvidos, o da conversa e o dos olhos. Só lhe resta um: o do coração." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Tempo virá em que os homens enlouquecerão e, ao verem alguém que não esteja louco, se erguerão contra ele: Tu és louco!" },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Já não temo a Deus, mas amo-O; pois a caridade expele o temor." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Sofrimentos, fome, nudez, vigílias, gemidos no coração — estas são as armas do monge." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem bate o ferro delibera primeiro o que fará; assim devemos deliberar qual virtude procuraremos adquirir, para não trabalharmos em vão." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "A obediência, unida à abstinência, submete as feras." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Conheço monges que caíram porque nutriram esperança nas próprias obras e negligenciaram interrogar o pai espiritual." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Se possível, o monge deve dizer aos anciãos quantos passos dá e quantas gotas bebe na cela, para se certificar de que não ofende." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Arsênio, foge dos homens, e serás salvo." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Arsênio, foge, cala-te, está tranquilo; pois estas são as raízes da impecância." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Ó Deus, não me abandones; nada fiz de bom em tua presença; mas, conforme a tua bondade, concede-me lançar o princípio." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Pela erudição do mundo nada temos; mas estes camponeses adquiriram as virtudes pelo próprio labor." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Com toda a força que tens, luta para que a tua labuta interior seja conforme Deus, e vencerás as paixões exteriores." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Se procurarmos a Deus, aparecer-nos-á; e, se o guardarmos, permanecerá conosco." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Deus sabe que vos amo; não posso, porém, estar com Deus e com os homens ao mesmo tempo." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "O monge basta dormir uma hora, se de fato é um lutador." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "De ter falado, arrependi-me muitas vezes; de me ter calado, nunca." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Enquanto a virgem está em casa de seu pai, muitos a querem desposar; se as coisas da alma são publicadas, não satisfazem a todos." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "O jejum está sempre comigo; a vós, porém, não posso guardar sempre. Ao receber o Cristo em vós, devo tratar-vos com toda a solicitude." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "Jamais fiz a própria vontade, nem ensinei a alguém o que antes não tivesse praticado." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "Enquanto falávamos de coisas celestiais, o sono vos dominava; quando escapou uma palavra ociosa, todos vos acordastes. Vigilai contra o torpor nas coisas espirituais." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "Deixaste de ser Senador, e não te fizeste monge." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Senta-te na cela; recorda-te do dia da morte, do desfalecimento do corpo e do silêncio terrível do inferno." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Deixa de ter relações com muitos homens, a fim de que a tua mente não se agite e perturbe a tranquilidade." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Grande coisa é rezar sem distração; ainda maior, porém, é salmodiar sem distração." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Recorda-te sempre de tua partida deste mundo e não esqueças o juízo eterno; assim não haverá pecado em tua alma." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Retira as tentações, e não haverá ninguém que se salve." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Um regime de vida duro e estável, unido à caridade, leva o monge ao porto da impassibilidade." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Fui falsamente acusado de engravidar uma virgem. Aceitei a humilhação e trabalhei para sustentá-la, até que a verdade foi revelada no parto." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Vi monges que há quarenta anos viviam no deserto sem sentir frio nem calor. Eles me disseram: se não podes o mesmo, senta-te em tua cela e chora os teus pecados." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Vi Satanás trajando uma túnica perpassada de vasos de tentações. Se um não agradar, apresentarei outro; e, se também este não agradar, apresentarei um terceiro." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "O demônio confessou: todos os irmãos são cruéis para comigo; e aquele que eu tinha como amigo tornou-se o mais cruel de todos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando o Abade Antônio viu o cordame que eu havia tecido durante a noite, exclamou: Muita força procede dessas mãos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando virdes uma cela construída perto do pântano, sabei que a devastação de Cétia está próxima; quando virdes meninos, tomai os mantos e retirai-vos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não foi por causa de mim; nada sou. Foi por causa da viúva e dos órfãos. Deus quer que a alma seja sem pecado; se ela é tal, recebe tudo o que pede." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Doze anos servi ao Senhor para receber a graça de acolher todos com simplicidade; e vós me aconselhais que a rejeite?" },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se houver vinho, bebe por causa dos irmãos; em troca de um cálice, deixa de beber água durante o dia. Em toda mortificação há lógica interior." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "O lenho que revira os gravetos em combustão é todo consumido pelo fogo. Assim, se o homem purifica a mente pelo temor de Deus, o próprio temor consome o corpo." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Os demônios fugiram confusos quando bati o esqueleto e disse: Levanta-te e vai para as trevas, se podes." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando estava exausto e disse a Deus que não podia ir mais longe, imediatamente me encontrei à beira do rio." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Fujam isto! — e entrou na cela. Quando perguntaram: para onde? Colocou o dedo nos lábios." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Ao repreender alguém com ira, satisfazes a própria paixão. Não é perdendo a ti mesmo que salvarás os outros." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Ao encontrar um ladrão saqueando minha cela, ajudei-o a carregar o camelo: nada trouxemos para este mundo; é evidente que nada podemos levar." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Na oração não é preciso falar muito; basta estender as mãos e dizer: Senhor, como queres e como sabes, compadece-te." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se guardamos a recordação dos males causados pelos homens, extinguimos a eficácia da recordação de Deus." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Vai ao túmulo e insulta os mortos; vai e louva esses mesmos mortos. Se queres ser salvo, torna-te morto: não consideres nem injúrias nem louvores." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "O que procuras já desapareceu dentre os monges." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Choremos, irmãos, e que os nossos olhos derramem lágrimas antes que nos vamos para aquele lugar onde elas consumirão os nossos corpos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Os demônios disseram: tudo que vós tendes, também nós o temos; apenas pela humildade diferis de nós, e por isso prevaleceis sobre nós." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Foge dos homens; senta-te em tua cela e chora os teus pecados. Não tenhas prazer na conversa. Assim serás salvo." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "O que quer que faças para te vingares do irmão, isso tudo voltará ao teu coração no tempo da oração." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "A oração é gérmen de mansidão e de carência de ira." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "A oração é defesa contra tristeza e desânimo." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Tudo que suportares sabiamente, colherás disso o fruto no tempo de oração." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Se queres rezar como deves, não deixes que a tristeza se apodere de tua alma." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Não queiras que as tuas coisas corram como te parece bem, mas como agrada a Deus. Assim serás isento de perturbação." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Feliz é o monge que se julga o rebotalho de todos." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "O monge que ama o retiro fica ileso dos dardos do inimigo; aquele que se junta às multidões recebe golpes contínuos." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Senta-te na tua cela e coloca em teu coração a ideia de que já há um ano que jazes no sepulcro." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Nada tenho que ver, pois morri. O morto não fala." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Dois homens tinham cada qual um morto em casa; um deles deixou o seu morto para ir chorar o do outro. Assim tu deixas teus próprios pecados para julgar os alheios." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "O sinal do monge manifesta-se nas tentações." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Glória a Deus pelo pecador que diz sem negar: pequei. Se lhe disseres: não percas o ânimo, excitarás sua alma à penitência." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "O homem que ensina mas não pratica é semelhante a uma fonte: a todos oferece com que beber, mas a si mesma não pode purificar." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Há o homem que parece calado, mas cujo coração condena os outros; esse está sempre a falar. E há outro que fala e guarda o silêncio da edificação." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Refrear-se, vigiar sobre si mesmo e usar de discernimento — estas três são as virtudes guias da alma." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Prostrar-se em presença de Deus, não se comparar e atirar para trás a vontade própria — são os instrumentos de que a alma se serve." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "A vitória sobre qualquer pena que te sobrevenha é calar-se." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Todo repouso do corpo é abominação aos olhos do Senhor." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "A compunção tem dois elementos: ela impele e também refreia." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "A distração é o começo dos males." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Preferirá calar-se o homem que se lembre do que está escrito: Pelas tuas palavras serás justificado, e pelas tuas palavras serás condenado." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "O que leva vida tranquila, o enfermo que dá graças, o que serve com mente pura — esses três realizam um só e mesmo grau de virtude." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Se o homem se arrepende de todo o coração e não torna a cometer o pecado, Deus o recebe já após três dias." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "A experiência ensina ao homem honesto." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Ainda que o homem fizesse um céu novo e uma nova terra, nunca poderia ficar sem tentações." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "O que é não retribuir o mal pelo mal? Tem quatro modalidades: a primeira provém do coração, a segunda da vista, a terceira da língua, a quarta é não fazer o mal em troca do mal." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Não habites em lugar onde vês que alguns têm inveja de ti; senão, não progredirás." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Os pensamentos que não executamos com o corpo, no decorrer do tempo extinguem-se. Como vestes fechadas num baú, apodrecem." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Não dês a mão aos pensamentos e eles serão frustrados; as tentações não nos podem ser nocivas enquanto não lhes prestamos cooperação." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Como o cervo no deserto devora serpentes e depois deseja as fontes de água, assim os monges, queimados pelo veneno dos demônios, desejam o Corpo e Sangue do Senhor." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "Quero que todos comam diariamente, mas pouco, sem saciarem-se. Esta é a via régia, porque suave." },
  { category: "patristic", author: "Abba Poimém", source: "Apotegmas", text: "O homem deve incessantemente respirar a humildade e o temor de Deus, do mesmo modo que inala e expele o ar pelas narinas." },
  { category: "patristic", author: "Abba Silvano", source: "Apotegmas", text: "Pode um homem todos os dias dar um início à vida boa? Se ele for diligente, pode todos os dias e a cada hora começar de novo." },
  { category: "patristic", author: "Abba Silvano", source: "Apotegmas", text: "Fui arrebatado ao céu e vi a glória de Deus. E lá permaneci até agora; agora fui mandado de volta." },
  { category: "patristic", author: "Abba Abraão", source: "Apotegmas", text: "Vai e pratica primeiro o que está escrito, depois volta e eu escreverei o restante." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "É por receio que, após edificantes palavras, sobrevenham conversas irrelevantes, que não permito que caminhes comigo." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "Vi monges fugindo e deixando suas celas abertas, sem fechar nem as janelas. Portanto, nada de tristeza pelas coisas materiais." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Maltrata esta pedra sem parar. Ela respondeu? Pois bem: assim também tu deves alcançar esta perfeição e imaginar que ninguém te ofende." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "A grande obra do homem é atirar a culpa sobre si mesmo diante de Deus e esperar a tentação até o último sopro de sua vida." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Aprendi grego e latim, mas não sei nem o alfabeto deste camponês egípcio — falando das virtudes práticas adquiridas pelo labor." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Senhor, mostra-me a estrada que conduz à salvação." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Havia como que uma depressão escavada em seu peito pelas lágrimas que caíram de seus olhos durante toda sua vida." },
  { category: "patristic", author: "Abba Arsênio", source: "Apotegmas", text: "Se alguém permanece na paz e ouve o grito de um passarinho, o seu coração não possui a paz; muito pior sois vós, que sois agitados como caniços." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "O início da salvação é condenar-se a si mesmo." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Reza com temor e tremor, com ardor, sobriedade e vigilância; sobretudo por causa dos inimigos invisíveis, que armarão ciladas exatamente neste ponto." },
  { category: "patristic", author: "Abba Apolo", source: "Apotegmas", text: "Vou trabalhar com Cristo hoje pela salvação de minha alma; pois esta é a recompensa que Ele dá." },
  { category: "patristic", author: "Abba Bessarião", source: "Apotegmas", text: "Deus está aqui; Deus está em todo lugar." },
  { category: "patristic", author: "Abba Epifânio", source: "Apotegmas", text: "Conhece-te a ti mesmo e nunca cairás. Dá trabalho à tua alma." }
];