import type { QuoteCard } from "@/types/content";

/** Citações dos Padres do Deserto (Apotegmas). */
export const DESERT_FATHERS_QUOTES: Omit<QuoteCard, "id">[] = [
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem senta no deserto e guarda o silêncio escapa a três guerras: a do ouvido, a da língua e a do coração." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A natureza do homem é fraca; é preciso orar para não cair na tentação." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Vai sentar-te na tua cela e a tua cela te ensinará tudo." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Arsenius, foge dos homens e serás salvo." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se quiseres ser monge, sê como o morto: não te indignes quando és ultrajado, não te ensoberbeças quando és louvado." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Nunca me deitei tendo algo contra alguém, e nunca deixei alguém deitar-se tendo algo contra mim." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Pela graça de Deus, desde que saí para o deserto, não proferi palavra de que me arrependa." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Sede como o publicano; humilhai o vosso espírito e sereis exaltados." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não tenta não progride; não há virtude sem tentação." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem que guarda rancor ao orar é como quem semeia no mar." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Eu sou como uma árvore plantada à beira da água; dou fruto na estação; minha folha não murcha." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como os querubins: todo olho." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração é o diálogo da mente com Deus." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não é o jejum que importa, mas a humildade; o que ganhas pelo jejum, perdes pela soberba." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência é a primeira das virtudes; por ela se expulsam os demônios e se abrem as portas do céu." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem habita no deserto e na solidão está livre de três guerras: a do ouvido, a da língua e a dos olhos." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não dês ao teu coração o que fere os teus olhos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se te repreenderem, não te justifiques; a justificação remove a penitência." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o teu coração com toda a diligência, porque dele procedem as fontes da vida." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não desprezes ninguém; quem despreza o outro peca." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "O tempo que passas sem oração é tempo perdido." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A tribulação que vem dos homens é melhor que a tranquilidade que vem da nossa vontade." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "O que não é útil para a alma, não o faças." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Quem edifica sobre a obediência edifica sobre a rocha." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Cada um cumpre o mandamento segundo a sua capacidade; Deus não rejeita a oferta." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas onde dorme a raiva; reconcilia-te antes do pôr do sol." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "Passei quatorze anos na Sceté suplicando a Deus noite e dia para me dar força de dominar a ira." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Se o homem não disser no seu coração: só eu e Deus existimos no mundo, não terá descanso." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não pode deixar os pais e os bens e renunciar a si mesmo não pode ser meu discípulo." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem deve dar a cada um o que lhe é devido: ao corpo, o trabalho; à alma, a oração; ao próximo, a caridade." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O pai que é duro consigo e misericordioso com os outros é um verdadeiro monge." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se te acusares, Deus te defenderá; se te justificares, Deus te acusará." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te deites com rancor no coração; vai e reconcilia-te, e depois volta." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "Não há outro caminho senão este: humilhar-se e dizer: perdoai-me." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem permanece na solidão e no silêncio escapa a três tentações: a do ouvido, a da língua e a da vista." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A soberba e a vanglória anulam todo o bem; a humildade apaga todo o mal." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "O monge é quem está separado de todos e unido a todos." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "O que é acima da tua força não o faças; guarda o que podes e serás salvo." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Não julgar ninguém, acolher a todos e rezar por todos: eis o caminho." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Nunca dormi tranquilo tendo contra alguém uma má palavra; antes ia reconciliar-me e depois descansava." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Se queres ser perfeito, vai vender o que tens, dá aos pobres e segue-me." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Faze o que puderes; Deus não exige o que está acima das tuas forças." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando quiseres edificar, começa por ti; não critiques os outros." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não fales mal de ninguém; quem fala mal do irmão peca gravemente." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem habita no deserto em silêncio está livre da maledicência, da vanglória e da curiosidade." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Arsenius, trabalha e não fales; o silêncio conduz à paz." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "O que sai da boca do homem é o que o contamina; guarda a tua língua e guardarás a tua alma." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Ameaça os teus pensamentos; não os deixes vaguear; traz-nos de volta à cela." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não há virtude maior que não julgar o próximo." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A tribulação é necessária; sem ela não entramos no reino dos céus." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Eu queria encontrar um homem e inclinar-me diante dele; nisso coloquei toda a minha esperança." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve rezar sem cessar; a oração contínua purifica o coração." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te aflijas por nada; confia em Deus e Ele te sustentará." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Reza em segredo; o Pai que vê no segredo te recompensará." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "A vida e a morte dependem do próximo; quem ganha o irmão ganha a Deus." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem que guarda a memória das injúrias é como quem bebe veneno esperando que o outro morra." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Nunca me deitei sem primeiro perdoar a quem me ofendeu." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A língua do monge está no seu coração; o que não sai do coração não sai da boca." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração é a supressão dos pensamentos." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Senta-te na tua cela e ela te ensinará a sabedoria; fuge e serás salvo." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser considerado alguém; esconde as tuas virtudes e serás em paz." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com alegria é a mãe de todas as virtudes." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Dá ao corpo o que precisa, não o que quer; e a alma crescerá." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não humilha o coração não pode rezar; a oração nasce da humildade." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te entregues à tristeza; a tristeza do mundo produz a morte; a tristeza segundo Deus produz a conversão." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "O que não edifica o próximo não o digas." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não busques ser honrado; busca ser humilde e Deus te exaltará." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A porta de Deus está sempre aberta; quem bate, entra." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Arsenius, fuge, cala-te e descansa; estas são as raízes da impecabilidade." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Ama o trabalho e a pobreza; deles nasce a liberdade do coração." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não perguntes sobre os pecados dos outros; pergunta sobre os teus." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te desculpes; quem se desculpa acusa-se a si mesmo." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem reza verdadeiramente não se lembra nem do corpo nem do que está fora; só de Deus." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve vigiar; a vigília afasta os maus pensamentos." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Não fales; a palavra é de prata, o silêncio é de ouro." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Cada um dá o que pode; Deus aceita a oferta conforme o coração." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Eu nunca dormi com algo contra o meu irmão; antes ia e pedia perdão." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não critiques ninguém; quem critica o irmão perde a paz." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A soberba precede a queda; a humildade precede a glória." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é aquela que não imagina nenhuma forma." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te aflijas pelo amanhã; o amanhã terá as suas aflições." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem reza de noite afasta as tentações do dia; quem negligencia a oração é vencido." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Quem se humilha será exaltado; quem se exalta será humilhado." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças ao outro o que não queres que te façam." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "O que está acima das tuas forças não o carregues; cumpre o que podes." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "O homem que guarda a boca guarda a alma; quem fala demais não tem paz." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "Muitas vezes me arrependi de ter falado; nunca me arrependi de ter calado." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se queres salvar a alma, considera-te pecador e submete-te a todos." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glorifies; a glória do homem é passageira." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como fogo; quem a guarda não peca." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia e ora cai na tentação; o espírito está pronto, a carne é fraca." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A compunção e as lágrimas purificam a alma; não as desprezes." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração; dele saem os bons e os maus pensamentos." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser pobre de espírito; os pobres de espírito possuem o reino dos céus." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "Não posso dominar-te, mas Deus pode; entrego-te a Ele." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Senta-te na tua cela; ora em segredo; o Pai te recompensará." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia constantemente cai nas armadilhas do demônio." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "A palavra do ancião tem poder; guarda-a no coração." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não te preocupes com o que os outros fazem; preocupa-te contigo mesmo." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a mãe da oração; a oração é a mãe da contemplação." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Quem se considera nada alcança tudo; quem se considera tudo não alcança nada." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro a obediência à oração; a obediência é a maior virtude." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Não julgues o teu irmão; julga-te a ti mesmo e encontrarás paz." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "A cela do monge é o seu refúgio; quem a abandona perde a paz." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Quem se humilha diante de todos será exaltado por Deus." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser vigilante; a negligência traz a ruína." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração sem distrações é a meta do monge." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Esconde as tuas virtudes como escondes os teus pecados." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem murmuração agrada a Deus." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda os teus pensamentos; eles determinam a tua vida." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada por vanglória; tudo faça por amor a Deus." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Cada um serve conforme a sua capacidade; Deus vê o coração." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva é como fogo; apaga-a antes que queime tudo." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência vence tudo; quem tem paciência tem tudo." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de mais nada." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge é aquele que se esquece de si mesmo." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e deixa o resto para Deus." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a porta do céu; quem passa por ela entra." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia a tudo não pode seguir a Cristo." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o que não podes mudar; aceita e reza." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa a si mesmo não precisa de acusadores." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho manual acompanha a oração; ambos são necessários." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares com ninguém; compara-te apenas com Deus." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser corrigido a corrigir; a correção humilha e salva." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como água viva; quem bebe não tem sede." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra Deus; quem busca o mundo perde Deus." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é melhor que o sacrifício; obedece e serás salvo." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como o sal; preserva e dá sabor." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura não conhece imagens; é pura contemplação." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser conhecido; esconde-te e Deus te conhecerá." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência alegre é mais valiosa que o jejum triste." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os teus pensamentos; eles são a raiz das ações." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem propósito; tudo deve servir a Deus." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus não olha para a quantidade, mas para o coração." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A reconciliação é melhor que a oferta; reconcilia-te primeiro." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A ira destrói a paz; quem domina a ira domina tudo." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não teme nada; Deus é suficiente." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro é aquele que não se vê a si mesmo." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses com o que não podes fazer; faz o que podes." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade abre todas as portas; a soberba fecha todas." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia cai; quem vigia permanece de pé." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes com o amanhã; vive o hoje em Deus." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será exaltado; quem se exalta será humilhado." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é ouro; a palavra é prata; usa-as com sabedoria." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories em nada; tudo vem de Deus." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser vencido a vencer; a derrota humilha e salva." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como luz; ilumina o caminho." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita no céu; não a abandones." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a chave do céu; obedece e entrarás." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a luz; brilha sem se mostrar." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira não tem palavras; é puro silêncio." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques ser visto; esconde-te e Deus te verá." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com amor é perfeita; obedece com alegria." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração; dele depende toda a vida." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada por ti mesmo; tudo faça por Deus." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus aceita o que ofereces com amor; o amor santifica tudo." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas com rancor; reconcilia-te antes de dormir." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é a mãe de todas as virtudes; cultiva-a." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de testemunhas." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge é invisível; não se mostra." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e descansa em Deus." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a rainha das virtudes; cultiva-a sempre." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode possuir; renuncia e possuirás." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o passado; vive o presente em Deus." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não precisa de juízes; acusa-te e serás justo." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho e a oração caminham juntos; não os separes." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares; cada um tem o seu caminho." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser humilhado a ser honrado; a humilhação salva." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como pão; alimenta a alma." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra paz; quem busca o mundo encontra guerra." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é o caminho; obedece e chegarás ao fim." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como o vento; passa sem deixar rastro." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é sem pensamentos; é pura atenção." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser famoso; esconde-te e serás conhecido por Deus." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem questionar é perfeita; obedece sem duvidar." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os pensamentos; eles determinam o destino." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem amor; o amor santifica tudo." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus vê o coração; oferece com amor e será aceito." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva é veneno; apaga-a antes que te mate." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência vence todas as dificuldades; tem paciência." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não teme a solidão." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro não existe; só existe Deus nele." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses; faz o que podes e confia em Deus." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a base; edifica sobre ela." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia não vê; quem vigia vê tudo." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes; confia em Deus e Ele cuidará de ti." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será levantado; quem se levanta será derrubado." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a melhor oração; cala-te e ouvirás Deus." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories; tudo é dom de Deus." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser corrigido a estar certo; a correção ensina." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como remédio; cura a alma." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita no paraíso; não a deixes." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a porta; obedece e passarás." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como o sol; brilha sem se exibir." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira é sem imagens; é pura presença." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques glória; esconde-te e serás glorificado por Deus." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com fé é poderosa; obedece com confiança." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração como guardas um tesouro; é precioso." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem propósito divino; tudo deve glorificar Deus." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus não olha para o tamanho, mas para o amor." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas sem paz; reconcilia-te e dormirás bem." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é ouro; cultiva-a e serás rico." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de explicações." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge desaparece; só Deus permanece." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e deixa o resto." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a coroa; usa-a sempre." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode receber; renuncia e receberás." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas; Deus está contigo sempre." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não tem inimigos; acusa-te e terás paz." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho manual é oração; trabalha e ora ao mesmo tempo." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares com os outros; compara-te com Cristo." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser vencido a vencer; a derrota é vitória." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como mel; adoça a alma." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra descanso; quem busca o mundo encontra cansaço." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a escada; obedece e subirás." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a água; flui sem resistência." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é sem palavras; é puro ser." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser admirado; esconde-te e serás amado por Deus." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem hesitação é perfeita; obedece imediatamente." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os pensamentos; eles são sementes de ações." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada por interesse; tudo faça por amor." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus aceita o que ofereces com humildade; a humildade santifica." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva queima; apaga-a com o perdão." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é força; quem tem paciência é forte." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de consolo humano." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro não se vê; só vê Deus." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses demais; descansa e trabalha com moderação." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a chave; abre todas as portas." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia não sabe; quem vigia sabe tudo." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes com o futuro; vive o presente." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será honrado; quem se honra será humilhado." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a melhor resposta; cala-te e ouvirás." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories em ti mesmo; gloria-te em Deus." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser humilhado a ser exaltado; a humilhação eleva." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como semente; planta-a no coração." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita na presença de Deus." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a estrada; obedece e caminharás." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a terra; recebe tudo sem reclamar." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira é sem formas; é pura essência." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques honra; esconde-te e serás honrado por Deus." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com gratidão é perfeita; obedece agradecendo." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração como guardas a vida; é precioso demais." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem devoção; tudo faça com amor." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus vê a intenção; oferece com pureza e será aceito." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas com mágoa; perdoa e dormirás em paz." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é sabedoria; quem tem paciência é sábio." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de aprovação." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge não existe para si; existe para Deus." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e confia." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a luz; ilumina o caminho." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode seguir; renuncia e seguirás." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o que não podes controlar; entrega a Deus." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não tem medo; acusa-te e terás coragem." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho é oração quando feito com devoção." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares; cada um tem a sua medida." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser corrigido a estar errado sozinho; a correção ajuda." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como espada; corta o mal." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem foge do mundo encontra liberdade; quem busca o mundo encontra escravidão." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é a ponte; obedece e passarás." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a lua; reflete a luz sem possuí-la." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração pura é sem conceitos; é pura realidade." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não queiras ser elogiado; esconde-te e serás louvado por Deus." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência sem reclamação é perfeita; obedece em silêncio." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Vigia os pensamentos; eles são portas do coração." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem caridade; tudo faça por amor ao próximo." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus não olha para o valor, mas para o coração que oferece." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "A raiva separa; o perdão une; escolhe o perdão." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é virtude; cultiva-a e serás virtuoso." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de reconhecimento." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O monge verdadeiro é transparente; só Deus se vê nele." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te canses inutilmente; trabalha com sabedoria." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a raiz; dela nasce tudo." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não vigia não entende; quem vigia entende tudo." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te preocupes; Deus conhece as tuas necessidades." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se humilha será exaltado por Deus; confia nisso." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O silêncio é a melhor pregação; cala-te e ensinarás." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te glories em nada terreno; gloria-te em Deus." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser vencido a vencer injustamente; a justiça importa." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A palavra do ancião é como bálsamo; cura as feridas." },
  { category: "patristic", author: "Abba Moisés", source: "Apotegmas", text: "Quem habita na cela habita no santuário; respeita-a." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "A obediência é o remédio; obedece e serás curado." },
  { category: "patristic", author: "Abba Bessarion", source: "Apotegmas", text: "O monge deve ser como a montanha; firme e silencioso." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "A oração verdadeira é sem distrações; é pura atenção a Deus." },
  { category: "patristic", author: "Abba José de Panefó", source: "Apotegmas", text: "Não busques ser importante; esconde-te e serás importante para Deus." },
  { category: "patristic", author: "Abba Nisteros", source: "Apotegmas", text: "A obediência com prontidão é perfeita; obedece sem demora." },
  { category: "patristic", author: "Abba Isaías", source: "Apotegmas", text: "Guarda o coração como guardas os olhos; é sensível." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Não faças nada sem discernimento; tudo faça com sabedoria." },
  { category: "patristic", author: "Abba Pior", source: "Apotegmas", text: "Deus aceita o que ofereces com simplicidade; a simplicidade agrada." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Não durmas com ódio; perdoa e dormirás tranquilo." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "A paciência é poder; quem tem paciência é poderoso." },
  { category: "patristic", author: "Abba Alónio", source: "Apotegmas", text: "Quem vive só com Deus não precisa de validação humana." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O verdadeiro monge não se busca; busca só a Deus." },
  { category: "patristic", author: "Abba Nau", source: "Apotegmas", text: "Não te sobrecarregues; faz o que podes e deixa Deus fazer o resto." },
  { category: "patristic", author: "Abba José", source: "Apotegmas", text: "A humildade é a porta; passa por ela e entrarás." },
  { category: "patristic", author: "Abba Antônio", source: "Apotegmas", text: "Quem não renuncia não pode ganhar; renuncia e ganharás." },
  { category: "patristic", author: "Abba Poimen", source: "Apotegmas", text: "Não te aflijas com o que não depende de ti; confia em Deus." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quem se acusa não tem orgulho; acusa-te e serás humilde." },
  { category: "patristic", author: "Abba Arsenius", source: "Apotegmas", text: "O trabalho manual é adoração quando feito com fé." },
  { category: "patristic", author: "Abba Sisoés", source: "Apotegmas", text: "Não te compares com ninguém; sê tu mesmo diante de Deus." },
  { category: "patristic", author: "Abba Agathon", source: "Apotegmas", text: "Prefiro ser humilhado a humilhar; a humildade é melhor." },
  { category: "patristic", author: "Abba Amun", source: "Apotegmas", text: "Suporta todo homem assim como Deus te suporta." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Como no primeiro dia em que te chegares a eles, assim todos os dias da tua vida conserva a tua qualidade de estranho, de modo a não teres familiaridade com eles. A familiaridade se parece com um calor muito ardente, o qual, sempre que se produz, afugenta a todos e destrói os frutos das árvores. Não há outro afeto pior do que a familiaridade; com efeito, é a matriz de todas as paixões." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: " preciso que o monge não deixe que a consciência o acuse em coisa nenhuma." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Sem a observância dos preceitos divinos, o homem não progride em uma só virtude que seja." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Nunca adormeci tendo alguma coisa contra alguém; nem, enquanto o podia, deixei que alguém fosse dormir tendo algo contra mim." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Aquelas (ser fornicador, soberbo, tagarela), eu as ponho em minha conta, pois são de proveito  à minha alma; ser herege, porém, significa separar-se de Deus, e não me quero separar de Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O homem se parece com uma árvore: o trabalho do corpo ê como que a folhagem, enquanto a disciplina da alma ê como que o fruto.  evidente que todo o nosso esforío deve visar ao fruto, isto ê,  disciplina da alma." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Desculpai-me, julgo que não há labor igual ao da oraíão a Deus. Pois, todas as vezes que o homem quer orar, tentam os inimigos arredá-lo; a oraíão, porém, atê o œltimo hálito requer luta." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Passou trÉs anos com uma pedra na boca atê que adquiriu o hábito do silÉncio." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Ao vender ou comprar objetos, dizia o preío apenas uma vez e recebia ou dava o valor em silÉncio e tranquilidade, sem proferir mais nada absolutamente." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Nunca dei um ágape; mas o dar e o receber eram para mim ágape: julgava que o lucro de meu irmão ê obra de frutificaíão." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O homem irascóvel, ainda que ressuscite um morto, não ê agradável a Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Vai, atira diante de Deus a tua fraqueza contra a fornicaíão, e encontrarás sossego." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Se alguém me fosse extremamente caro, e eu soubesse que ele me leva ao pecado, eu o afastaria de mim." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: " preciso que, a toda hora, o homem se recorde do juízo de Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Se me fosse possóvel encontrar um leproso e dar-lhe o meu corpo em troca do corpo dele, fá-lo-ia com prazer. Pois esta ê a caridade perfeita." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Atê agora fiz o que pude para observar os mandamentos de Deus; sou homem, porém; como hei de saber se meu esforío agradou a Deus? Pois um ê o modo de julgar de Deus, outro o dos homens." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Vai, e nutre em teu espórito os pensamentos que os malfeitores no cárcere revolvem. Perguntam sempre: quando há de vir o Diretor? E choram por causa da espera. Assim também o monge em todo tempo deve arguir a sua alma, dizendo: Ai de mim! Como posso apresentar-me ao tribunal de Cristo? Se com tais pensamentos estiver sempre ocupado o teu espórito, poderás ser salvo." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Passei na Cêtia quatorze anos, pedindo a Deus, dia e noite, que me concedesse vencer a ira." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Senta-te em tua cela, come um pouco diariamente, e conserva a palavra do publicano em teu coraíão. Assim poderás ser salvo." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Para que não esteja eu sempre a vaguear na medida em que se precipitam os meus pensamentos Ñ esta ê também uma liíão para que com estabilidade caminhemos na via do Senhor." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Rogo-te, Senhor meu Deus, não deixes perecer a tua criatura." },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "Quanto esforío fiz no deserto para possuir esta loucura; e eis que por causa de ti estou para a perder hoje!" },
  { category: "patristic", author: "Abba Amonas", source: "Apotegmas", text: "A via estreita e atribulada ê esta: Fazer violÉncia aos seus pensamentos, e, por causa de Deus, cortar a vontade própria.  assim que se realiza o dito: Eis que deixamos tudo, e seguimos a ti." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Senhor, quero ser salvo, e não me deixam os pensamentos; que farei na minha tribulaíão? Como serei salvo?" },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Antão, cuida de ti mesmo, pois isto são juízos de Deus, e não te convêm penetrá-los." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Em qualquer lugar para onde vás, tem sempre Deus ante os olhos; qualquer coisa que faías, procura ter o testemunho das Sagradas Escrituras: e, de qualquer lugar em que residas, não te movas facilmente." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Esta ê a grande labuta do homem: assumir sobre si mesmo a culpa própria diante de Deus, e esperar a tentaíão atê o œltimo suspiro." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Ninguêm sem tentaíão poderá entrar no reino dos cêus. Suprime as tentaíões, e ninguêm será salvo." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Não confies em tua justiía, nem te arrependas do que já passou, e sÉ continente de lóngua e de ventre." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Vi todas as armadilhas do inimigo estendidas sobre a terra, e, gemendo, perguntei: Quem escapará a elas? Ouvi então uma voz que me dizia: A humildade." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Há alguns que consomem o corpo em ascese, e, por não terem discriíão, se afastam de Deus." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Do próximo dependem a vida e a morte; pois, se lucramos o nosso irmão, lucramos a Deus. Se, porém, escandalizamos o irmão, pecamos contra Cristo." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Como os peixes que se demoram na terra seca morrem, assim também os monges, permanecendo fora da cela ou vivendo com seculares, se relaxam da tensão da vida recolhida.  preciso, pois, que, como o peixe para o mar, assim também nos apressemos para a cela." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Quem permanece no deserto e lá vive tranquilamente, liberta-se de trÉs combates: o dos ouvidos, o da conversa e o dos olhos; só lhe resta um: o da fornicaíão." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Tempo virá em que os homens enlouquecerão, e, ao verem alguém que não esteja louco, se erguerão contra ele, dizendo: Tu ês louco, porque não será semelhante a eles." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Já não temo a Deus, mas amo-o. Pois a caridade expele o temor." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Tem sempre ante os olhos o temor de Deus. Odiai o mundo e tudo que nele há. Odiai todo repouso da carne. Renunciai a esta vida, a fim de viverdes para Deus. Sofrei fome, sede, suportai a nudez, vigiai, arrependei-vos, chorai, gemei no vosso coraíão." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Quem bate a massa de ferro delibera primeiro o que fará: foice, espada ou machado. Assim também nós devemos deliberar qual virtude procuraremos adquirir, para que não trabalhemos em vão." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "A obediÉncia, unida  abstinÉncia, submete as feras." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Conheío monges que, após muitas fadigas, caóram, porque nutriram esperanía nas suas próprias obras e negligenciaram o preceito de interrogar seu pai espiritual." },
  { category: "patristic", author: "Abba Antão", source: "Apotegmas", text: "Se possóvel, o monge deve dizer aos anciãos quantos passos dá ou quantas gotas bebe na cela, para se certificar de que não ofende nessas práticas." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "ArsÉnio, foge dos homens, e serás salvo." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "ArsÉnio, foge, cala-te, está tranquilo, pois estas são as raózes da impecéncia." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "î Deus, não me abandones; nada fiz de bom em tua presenía; mas, conforme a tua bondade, concede-me laníar o princópio." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Nós pela erudiíão do mundo nada temos, enquanto estes, os camponeses e egópcios, pelo próprio labor ê que adquiriram as virtudes." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Com toda a foría que tens, luta para que a tua labuta interior seja conforme Deus, e vencerás as paixões exteriores." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Se procurarmos a Deus, aparecer-nos-á; e, se o guardarmos, permanecerá conosco." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Deus sabe que vos amo; não posso, porém, estar com Deus e com os homens. Não posso, por conseguinte, deixar Deus e vir para junto dos homens." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "O monge basta dormir uma hora, se, de fato, ê um lutador." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "De ter falado, arrependi-me muitas vezes; de me ter calado, nunca." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Enquanto a virgem está em casa de seu pai, muitos são os que a querem desposar; se as coisas da alma são publicadas, não podem satisfazer a todos." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "O jejum está sempre comigo; a vós, porém, não posso guardar sempre. O jejum ê coisa œtil e necessária; depende da nossa própria vontade; o exercócio da caridade, ao contrário, a Lei de Deus no-lo impõe com necessidade. Portanto, ao receber em vós o Cristo, devo tratar-vos com toda a solicitude." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "Jamais fiz a própria vontade, nem ensinei a alguém o que antes não tivesse praticado." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "Enquanto falávamos de coisas celestiais, os olhos de todos vós estavam dominados pelo sono; quando, porém, escapou uma palavra ociosa, todos com énimo vos acordastes. Irmãos, reconhecei a aíão do mau demùnio, e vigiai contra o torpor nas coisas espirituais." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "Deixaste de ser Senador, e não te fizeste monge." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Quando estiveres sentado na cela, recolhe o teu espórito, recorda-te do dia da morte, considera o desfalecimento do corpo, pensa na desgraía. Recorda-te também da situaíão no inferno: medita como lá estão as almas, em que mui terróvel silÉncio, em que acutóssimo gemido." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Deixa de ter relaíões com muitos homens, a fim de que a tua mente não se torne agitada, e perturbe a habitual tranquilidade." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Grande coisa ê rezar sem distraíão; ainda maior, porém, ê salmodiar sem distraíão." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Recorda-te sempre de tua partida deste mundo, e não esqueías o juízo eterno; assim não haverá pecado em tua alma." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Retira as tentaíões, e não haverá ninguêm que se salve." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Um regime de vida duro e não inconstante, unido  caridade, leva muito rapidamente o monge ao porto da apatia Ñ vitória sobre as paixões." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Fui falsamente acusado de engravidar uma virgem. Aceitei a humilhaíão, trabalhando para sustentá-la, atê que a verdade foi revelada durante o parto. Então fugi para a Cêtia, a fim de que não me atribulassem os homens. Este ê o motivo pelo qual vim para cá." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Eu ainda não me tornei monge, mas vi monges Ñ dois homens nus que há quarenta anos viviam no deserto profundo, sem sentir frio no inverno nem calor no verão. Eles me disseram: Se não podes fazer o mesmo que nós, senta-te em tua cela e chora os teus pecados." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Vi Satanás sob forma de homem, trajando uma tœnica de linho perpassada de furos, dos quais pendiam pequenos vasos de tentaíões para os irmãos. Disse-me: Se um não agradar a alguém, apresentarei outro; e, se também este não agradar, apresentarei um terceiro." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Ao retornar depois de visitar Teopempto, o demùnio confessou: Todos os irmãos são cruêis para comigo; e o maior mal ê que também aquele que eu tinha como amigo obediente tornou-se o mais cruel de todos. Jurei não andar mais por aqueles lugares." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando o Abade Antão viu a grande quantidade de cordame que eu havia tecido durante a noite, exclamou: Muita foría procede dessas mãos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando virdes uma cela construóda perto do péntano, sabei que a devastaíão de Cêtia está próxima; quando virdes árvores, a devastaíão estará s portas; quando virdes meninos, tomai os vossos mantos e retirai-vos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não foi por causa de mim que isto aconteceu, pois nada sou; mas foi por causa da viœva e dos órfãos que Deus o fez. Deus quer que a alma esteja sem pecado; e, se ela ê tal, recebe tudo o que pede." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Doze anos servi ao meu Senhor para que me desse a graía de receber todos com simplicidade; e todos vós me aconselhais que a rejeite?" },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se houver vinho, bebe por causa dos irmãos, e, em troca de um cálice de vinho, deixa de beber água durante o dia. Em toda mortificaíão, há lógica interior." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "O lenho que revira os gravetos em combustão ê todo consumido pelo fogo. Assim também se o homem purifica a sua mente pelo temor de Deus, o próprio temor de Deus consome o corpo dele." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Ao dormir em templo pagão usando um caixão como travesseiro, quando os demùnios chamavam a outros dizendo: levanta-te, eu com muita coragem batia o esqueleto e dizia: Levanta-te e vai para as trevas, se podes. Eles fugiram confusos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Quando estava exausto de carregar cestos e disse a Deus que não podia ir mais longe, imediatamente me encontrei  beira do rio." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Fujam, irmãos. E quando perguntaram: para onde? Coloquei o dedo nos lábios e disse: Fujam isto Ñ e entrei na cela." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se, ao repreender alguém, ês movido  ira, satisfazes a própria paixão. Não ê perdendo a ti mesmo que salvarás os outros." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Ao encontrar um ladrão saqueando minha cela, ajudei-o a carregar o camelo e disse: Nada trouxemos para este mundo; ê evidente que também nada podemos levar. O Senhor deu; como Ele quis, assim se fez." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Não ê preciso falar muito na oraíão, mas estender as mãos e dizer: Senhor, como queres e como sabes, compadece-te. Ou, se estiveres tentado: Senhor, vem em meu socorro. Ele mesmo sabe o que convêm." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Se guardamos a recordaíão dos males que nos são acarretados pelos homens, extinguimos a eficácia da recordaíão de Deus. Se, porém, conservamos a lembranía dos males causados pelos demùnios, somos invulneráveis." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Vai para junto do tœmulo e insulta os mortos... Agora, vai e louva esses mortos... Assim também tu, se queres ser salvo, torna-te morto: como os mortos, não consideres nem as injœrias dos homens nem os seus louvores. Assim poderás ser salvo." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "O que procuras já desapareceu dentre os monges." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Choremos, irmãos, e que os nossos olhos derramem lágrimas antes que nos vamos para aquele lugar em que as nossas lágrimas consumirão de fora os nossos corpos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Os demùnios disseram: Tudo que vós tendes, também nós o temos; apenas pela humildade diferis de nós, e prevaleceis sobre nós." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Foge dos homens; senta-te em tua cela e chora os teus pecados. E não tenhas prazer na conversa com os homens. Assim serás salvo." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "O que quer que faías para te vingares do irmão que te injuriou, isso tudo voltará ao teu coraíão no tempo da oraíão." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "A oraíão ê gêrmen de mansidão e de carÉncia de ira." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "A oraíão ê defesa contra tristeza e desénimo." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Tudo que suportares sabiamente, recolherás disso o fruto no tempo de oraíão." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Se queres rezar como deves, não deixes que a tristeza se apodere de tua alma; se deixas, ê em vão que corres." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Não queiras que as tuas coisas corram como te parece bem, mas como agrada a Deus. Assim serás isento de perturbaíão e agradável em tua oraíão." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Feliz ê o monge que se julga o rebotalho de todos." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "O monge que ama o retiro, fica ileso dos dardos do inimigo. Aquele, porém, que se junta s multidões, recebe golpes contónuos." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Poimêm, ainda vives? Vai, senta-te em tua cela e coloca em teu coraíão a ideia de que já há um ano que jazes no sepulcro." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Nada tenho que ver, pois morri. Ora o morto não fala." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Para que grande ancião e eu não conflitemos: Preparai pequenas poríões de alimento, tomai uma medida de vinho, e vamos ter com o ancião. Talvez assim lhe possamos dar agrado." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Havia dois homens, os quais tinham cada qual um morto em sua casa; um deles deixou o seu morto para ir chorar o do outro. Assim também tu deixas teus próprios pecados para julgar os alheios." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "O sinal do monge se manifesta nas tentaíões." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Glória a Deus pelo pecador que sabe que pecou e, sem negar, diz: pequei. Pois, se a ele disseres: não percas o énimo, mas guarda-te para o futuro, excitarás sua alma  penitÉncia." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Se o homem pecar e o negar, dizendo: não pequei, não o acuses, pois lhe cortarias o énimo. Se, ao contrário, lhe disseres: não percas énimo, irmão, mas guarda-te para o futuro, excitarás a sua alma  penitÉncia." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "O homem que ensina, mas não pratica o que ensina, ê semelhante a uma fonte: a todos oferece com que beber e se lavar, mas a si mesma não pode purificar." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Há o homem que parece calado, mas cujo coraíão condena os outros Ñ esse tal está sempre a falar. E há outro que fala da manhã  noite, mas guarda o silÉncio, isto ê, nada diz que não seja de edificaíão." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Refrear-se, vigiar sobre si mesmo, e usar de discernimento Ñ estas trÉs virtudes são as guias da alma." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Prostrar-se em presenía de Deus, não se comparar, e atirar para trás a vontade própria Ñ são instrumentos de que se serve a alma." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "A vitória sobre qualquer pena que te sobrevenha ê calar-se." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Todo repouso do corpo ê abominaíão aos olhos do Senhor." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "A compuníão tem dois elementos: ela impele e também refreia." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "A distraíão ê o comeío dos males." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Preferirá calar-se o homem que se lembre do que está escrito: Pelas tuas palavras serás justificado, e pelas tuas palavras serás condenado." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Se trÉs se reœnem, dos quais um leva dignamente uma vida tranquila, o outro ê doente e dá graías a Deus, o terceiro faz o seu serviío com mente pura Ñ esses trÉs realizam um só e idÉntico grau de virtude." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Como o espadeiro do rei a este assiste pronto em todo tempo, assim ê preciso que a alma esteja pronta diante do demùnio da fornicaíão." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Se o homem se arrepende de todo o coraíão e não torna a cometer o pecado, Deus o recebe já após trÉs dias." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Antes de ir para o Ofócio, sentava-se a sós, ficando a examinar os seus pensamentos durante cerca de uma hora. Depois disto ê que se punha a caminho." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "A experiÉncia ensina ao homem honesto." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Ainda que o homem faía um cêu novo e uma nova terra, nunca poderia ficar sem tentaíões." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "O que ê não retribuir o mal pelo mal? Esse vócio tem quatro modalidades: a primeira provêm do coraíão; a segunda, da vista; a terceira, da lóngua; e a quarta ê não fazer o mal em troca do mal." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Não habites em lugar onde vÉs que alguns tÉm inveja de ti; senão, não progredirás." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Os pensamentos que não executamos com o nosso corpo, no decorrer do tempo extinguem-se ou apodrecem. Como vestes fechadas num baœ, apodrecem com o tempo." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Não dÉs a mão aos pensamentos e eles serão frustrados Ñ as tentaíões não nos podem ser nocivas, enquanto não lhes prestamos nossa cooperaíão." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Como o cervo no deserto devora muitas serpentes e depois deseja as fontes das águas, assim também os monges são queimados pelo veneno dos maus demùnios e desejam o sábado e o domingo para se chegarem ao Corpo e ao Sangue do Senhor, a fim de se purificarem." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Quero que todos comam diariamente, mas pouco, de modo a não ficarem saciados. Esta ê a via rêgia, porque suave." },
  { category: "patristic", author: "Abba Poimêm", source: "Apotegmas", text: "Um homem deve incessantemente respirar a humildade e o temor de Deus do mesmo modo que inala e expele o ar atravês das narinas." },
  { category: "patristic", author: "Abba Silvano", source: "Apotegmas", text: "Pode um homem, todos os dias, dar um inócio  vida boa? Respondeu: Se ele for diligente, pode todos os dias e a cada hora comeíar a vida boa de novo." },
  { category: "patristic", author: "Abba Silvano", source: "Apotegmas", text: "Fui arrebatado ao cêu e vi a glória de Deus. E lá permaneci atê agora, e agora fui mandado de volta." },
  { category: "patristic", author: "Abba Abraão", source: "Apotegmas", text: "Vai e pratica primeiro o que está escrito, depois volte e eu escreverei o restante." },
  { category: "patristic", author: "Abba Abraão", source: "Apotegmas", text: "Falou corretamente sobre desprezar as próprias asceses passadas. Pois agora já lhe ê possóvel levantar as duas mãos a fim de que o trigo cresía com abundéncia pelo mundo inteiro e pedir a Deus que perdoe os pecados de toda esta geraíão." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: " por receio que, após tão edificantes palavras, sobrevenham conversas irrelevantes, que eu não permito que caminhes comigo." },
  { category: "patristic", author: "Abba Ammoes", source: "Apotegmas", text: "Vi monges fugindo, deixando suas celas lavadas e também seus pergaminhos, sem nem fechar as janelas. Portanto nada de tristeza pelas coisas materiais." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O abade Antùnio disse ao abade Amon: Tu terás muito progresso no temor de Deus. Depois o mandou insultar uma pedra. Como a pedra não respondeu, o ancião disse: Assim também tu deves alcaníar esta perfeiíão e imaginar que ninguêm te ofende." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A casa deles não tem portas: entra quem quer na estalagem e solta o burro! Ñ falava assim dos irmãos que diziam tudo o que lhes vinha  cabeía." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A grande obra do homem ê atirar a culpa sobre si mesmo diante de Deus e esperar a tentaíão atê o œltimo sopro de sua vida." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Nas noites de sábado, preparando-se para a glória do domingo, virava-se de costas para o sol e elevava as mãos em oraíão em direíão ao cêu, atê que o sol novamente brilhasse em sua face. Então se sentava." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Realmente aprendi Grego e Latim, mas não sei nem ao menos o alfabeto desse camponÉs egópcio Ñ falando das virtudes práticas adquiridas pelos simples pelo labor." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Senhor, mostra-me a estrada que conduz  salvaíão." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Diziam dele que havia como que uma depressão escavada em seu peito pelas lágrimas que caóram de seus olhos durante toda sua vida." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Na verdade, se alguém permanece na paz e ouve o grito de um passarinho, o seu coraíão não possui a paz. Pior ainda sois vós, que sois agitados como esses caniíos." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "O inócio da salvaíão ê condenar-se a si mesmo." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Retirem-se as tentaíões e ninguêm será salvo." },
  { category: "patristic", author: "Abba Evágrio", source: "Apotegmas", text: "Se te foge a coragem, reza. Reza com temor e tremor, com ardor, sobriedade e vigiléncia. Assim deve-se rezar, sobretudo por causa dos nossos inimigos invisóveis, que nos armarão ciladas exatamente neste ponto." },
  { category: "patristic", author: "Abba Amon", source: "Apotegmas", text: "Suporta todo homem assim como Deus te suporta." },
  { category: "patristic", author: "Abba Apolo", source: "Apotegmas", text: "Vou trabalhar com Cristo hoje, pela salvaíão de à minha alma, pois esta ê a recompensa que Ele dá." },
  { category: "patristic", author: "Abba Bessarião", source: "Apotegmas", text: "Deus está aqui; Deus está em todo lugar." },
  { category: "patristic", author: "Abba Epifénio", source: "Apotegmas", text: "Conhece-te a ti mesmo e nunca cairás. Dá trabalho  tua alma Ñ a oraíão contónua e o amor de Deus Ñ antes que alguém a leve a maus pensamentos; e reza para que o espórito do erro se afaste de ti." },
  { category: "patristic", author: "Abba Gregório", source: "Apotegmas", text: "Que a tua obra seja pura pela presenía do Senhor e não pela exibiíão." },
  { category: "patristic", author: "Abba Isidoro", source: "Apotegmas", text: "Quando eu era mais jovem e permanecia em minha cela, não punha limites  oraíão: a noite era para mim tempo de oraíão tanto quanto o dia." },
  { category: "patristic", author: "Abba Isidoro de Pelœsia", source: "Apotegmas", text: "Prezem as virtudes e não sejam escravos da glória; pois as primeiras são imortais enquanto que as œltimas logo se desvanecem." },
  { category: "patristic", author: "Abba Isidoro, o sacerdote", source: "Apotegmas", text: "Se vocÉ jejua regularmente, não se encha de orgulho. Se vocÉ se vangloria por causa do jejum, então ê melhor que coma carne.  melhor para um homem comer carne do que se inflar com orgulho e gloriar-se de si mesmo." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Gostaria de ser livre de todos os cuidados, como os anjos, que não trabalham, mas prestam culto a Deus incessantemente." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Sou como um homem sentado debaixo de uma grande árvore que vÉ bestas selvagens e serpentes vindo contra ele. Quando não pode mais, corre e sobe na árvore e se salva.  a mesma coisa comigo: quando não tenho forías contra os pensamentos maus, busco refœgio em Deus pela oraíão e sou salvo do inimigo." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Senhor, dai-me foría para a luta." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Pusemos a carga leve de um lado, que ê a auto-acusaíão, e nos carregamos com um grande peso, que ê a auto-justificaíão." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Humildade e temor de Deus estão acima de todas as virtudes." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Vigiar significa sentar-se na cela e estar sempre presente a Deus. Isto ê o que significa o dito: eu vigiava, e Deus veio atê mim." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Uma casa não ê construóda do topo para baixo. Deve-se comeíar com a fundaíão. A fundaíão ê nosso próximo, a quem devemos ganhar, e este ê o lugar para comeíar. Pois todos os mandamentos de Cristo dependem desse um." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Mesmo que sejamos completamente desprezados aos olhos dos homens, alegremo-nos, pois somos honrados aos olhos de Deus." },
  { category: "patristic", author: "Abba João, o Anão", source: "Apotegmas", text: "Dizia-se que certa vez ele teceu corda para uma cesta atê o teto sem perceber, pois seu espórito estava absorto em profunda contemplaíão." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Perguntaram a Pai Macário: Como devemos rezar? O ancião disse: Não ê necessário fazer grandes discursos; ê suficiente erguer as mãos e dizer: Senhor, como desejas e como conheces, tenha piedade. E se o conflito crescer, digam: Senhor, ajuda! Ele sabe muito bem o que precisamos." },
  { category: "patristic", author: "Abba Macário", source: "Apotegmas", text: "Ao encontrar um homem saqueando seus bens, ajudou-o a carregar o animal e disse: Não trouxemos nada para este mundo, e não podemos levar nada. Assim o despediu com grande paz." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "Quaisquer que sejam teus sofrimentos, a vitória sobre eles está no silÉncio." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "O homem deve incessantemente respirar a humildade e o temor de Deus do mesmo modo que inala e expele o ar atravês das narinas." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "Prostrar-se diante de Deus, não dar-se nenhuma importéncia, jogar fora a própria vontade: eis os instrumentos com os quais a alma pode trabalhar." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "Desprezar-se a si mesmo consiste em rebaixar-se abaixo dos animais e saber que eles não serão condenados." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "A humildade ê a terra que o Senhor requisitou para realizar o sacrifócio." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "A hesychia, a oraíão, o amor e a temperanía são o quádruplo coche que eleva aos cêus a inteligÉncia." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "Se não podes prender o vento, muito menos podes impedir o surgimento dos pensamentos maus; mas o que podes fazer ê resistir-lhes." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "Um machado pode gloriar-se de alguma coisa sem aquele que o usa? Pois bem: não cultives esses pensamentos e ficarão sem efeito em ti." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "Aquele que lamenta sempre e somente a si mesmo pode viver em qualquer lugar. Mas, se se glorifica, então não se dará bem em lugar algum." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "A alma não alcanía a humildade em nada se não lhe racionares o pão, isto ê, se não a reduzires a alimentar-se apenas do necessário." },
  { category: "patristic", author: "Abba Pastor (Poemen)", source: "Apotegmas", text: "O inócio do mal ê não ouvir." },
  { category: "patristic", author: "Madre Sinclêtica", source: "Apotegmas", text: "No comeío, há luta e muito trabalho para os que se aproximam de Deus. Mas, depois disso, há uma indescritóvel alegria." },
  { category: "patristic", author: "Madre Sinclêtica", source: "Apotegmas", text: "Assim como para acender uma fogueira no inócio há muita fumaía e seus olhos lacrimejam, assim devemos acender o fogo divino em nós mesmos com lágrimas e esforío." },
  { category: "patristic", author: "Madre Sinclêtica", source: "Apotegmas", text: " possóvel ser solitário em sua própria mente mesmo no meio de uma multidão; e ê possóvel para um solitário viver na multidão de seus próprios pensamentos." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O fim œltimo da nossa profissão ê o reino de Deus ou dos cêus. Quanto ao nosso escopo, ê a pureza de coraíão, sem a qual ê impossóvel alguém alcaníar aquele fim." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Não ê tão grande o lucro do jejum quanto os dispÉndios da ira; nem tanto o fruto que se colhe com a leitura quanto o dano que sofremos com o desprezo de um irmão." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: " impossóvel, sem dœvida, que a mente não seja perturbada por pensamentos. Mas a quem se empenha, ê possóvel acolhÉ-los ou rejeitá-los." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O reino de Deus não virá de modo visóvel... o reino de Deus está dentro de vós." },
  { category: "patristic", author: "Abba Doroteus", source: "Apotegmas", text: "Ë medida que as pessoas aproximam-se do centro Ñ Deus Ñ com seu amor, atravês dessa mesma medida elas se aproximam entre si. Ao contrário, quanto mais as pessoas se afastam entre si com desavenías, simultaneamente mais elas se distanciam de Deus." },
  { category: "patristic", author: "São Macário o Grande", source: "Apotegmas", text: "A alma que ama sinceramente a Deus, mesmo que tenha realizado milhares de boas obras, devido  sua insatisfeita ansiedade por Deus, considera a si própria como se nunca tivesse realizado nada." },
  { category: "patristic", author: "São João Clómaco", source: "Apotegmas", text: "O monge ê aquele que está separado de todos e unido a todos." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "Um desejo totalmente voltado para Deus religa a Deus e religa entre si queles que desejam." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "Aquele que abeníoa de boca e menospreza em seu coraíão esconde a hipocrisia sob o vêu do amor." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "Só o amor une a criaíão a Deus e os seres entre si na concórdia." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "O amor e a temperanía purificam a alma, mas a oraíão pura ilumina a inteligÉncia." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "Se queres vencer os pensamentos apaixonados, adquire a temperanía e o amor ao próximo." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "Da mesma maneira que não se pode sentir perfumes na lama, não ê possóvel sentir em uma alma rancorosa o bom odor do amor." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "O trabalho oculto suprime a vanglória, e não menosprezar ninguêm rechaía o orgulho." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "A hesychia, a oraíão, o amor e a temperanía são o quádruplo coche que eleva aos cêus a inteligÉncia." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "A obra própria da inteligÉncia ê sempre estudar as palavras de Deus." },
  { category: "patristic", author: "Abba Thalassius", source: "Apotegmas", text: "A apatheia ê a imobilidade da alma face a face do mal.  impossóvel alcaníá-la sem a compaixão do Cristo." },
  { category: "patristic", author: "São Gregório", source: "Apotegmas", text: "Deus exige trÉs coisas de todo homem que recebeu o batismo: a fê direita para a alma, a franqueza para a lóngua, a castidade para o corpo." },
  { category: "patristic", author: "Abba Zacarias", source: "Apotegmas", text: "Em minha opinião, ê em tudo se fazer violÉncia Ñ eis o monge." },
  { category: "patristic", author: "Abba Teodoro de Farmêia", source: "Apotegmas", text: "Observava acima de muitos outros estes trÉs princópios: a pobreza, a ascese, a fuga dos homens." },
  { category: "patristic", author: "Abba João o Nanico", source: "Apotegmas", text: "Ao te levantares a cada manhã, recomeía a perseguir as virtudes com grande perseveranía. Suporta a injœria sem te pores em cólera. Encerra-te no sepulcro como se já fosses morto." },
  { category: "patristic", author: "Abba Josê de Tebas", source: "Apotegmas", text: "Aos olhos de Deus, trÉs castas de gentes são honradas: em primeiro os doentes que padecem as tentaíões e aceitam os males rendendo aíões de graías; em segundo os de aíão pura diante de Deus; enfim os que permanecem na submissão do pai espiritual." },
  { category: "patristic", author: "Abba João (Higœmeno)", source: "Apotegmas", text: "Jamais fiz minha própria vontade e nada ensinei que antes não houvesse eu mesmo praticado." },
  { category: "patristic", author: "Abba Nisteros o Grande", source: "Apotegmas", text: "Ao que quer que aspires na alma segundo Deus, fá-lo e guarda teu coraíão." },
  { category: "patristic", author: "Abba Pastor (Poemão)", source: "Apotegmas", text: "A pobreza, as provaíões e a discriíão são as trÉs obras da vida solitária. Se essas trÉs obras se encontram num homem, Deus habita nele." },
  { category: "patristic", author: "Abba Pastor (Poemão)", source: "Apotegmas", text: "Se o monge odeia duas coisas, liberta-se deste mundo: o bem-estar e a vã glória." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Desde que estou neste deserto, não me lembra ter comido pão sem havÉ-lo ganho com minhas mãos; nem atê agora ter lamentado uma palavra dita." },
  { category: "patristic", author: "Abba Sisóes", source: "Apotegmas", text: "Reputa-te por nada, lanía tua vontade atrás de ti, permanece sem preocupaíões Ñ e repousarás." },
  { category: "patristic", author: "Abba Chamê", source: "Apotegmas", text: "Não habiteis com os herêticos, não tenhais relaíões com os grandes, não estendais as mãos para receber mas antes para dar." },
  { category: "patristic", author: "Um Ancião", source: "Apotegmas", text: "Se alguém possui a humildade e a pobreza e não julga os outros, o temor de Deus virá sobre ele." },
  { category: "patristic", author: "Abba Euprêpios", source: "Apotegmas", text: "Que o temor, a humildade, a privaíão de alimento e o pentos Ñ tristeza pelos pecados Ñ faíam morada em ti." },
  { category: "patristic", author: "Um Ancião", source: "Apotegmas", text: "Não faías a outrem o que detestas. Basta observar esta palavra para ser salvo." },
  { category: "patristic", author: "Um Ancião", source: "Apotegmas", text: "A vida do monge ê o trabalho, a obediÉncia e a meditaíão; ê não julgar, nem reclamar, nem murmurar. Seus olhos são cegos para o mal." },
  { category: "patristic", author: "Um Ancião", source: "Apotegmas", text: "Ora a Deus para que te meta no coraíão o pentos e a humildade. Não tenhas familiaridade com mulheres, nem com crianías, nem com herêticos. Detêm a lóngua e o apetite; priva-te do vinho." },
  { category: "patristic", author: "Um Ancião", source: "Apotegmas", text: "Vá, implore a Deus que lhe envie as lutas de modo que vocÉ recupere a afliíão e humildade que vocÉ possuóa, pois ê pela luta que as almas progridem." },
  { category: "patristic", author: "Abba Paêsio", source: "Apotegmas", text: "Desde que vivo solitário, o sol nunca me viu comer." },
  { category: "patristic", author: "São Basólio", source: "Apotegmas", text: "Deixaste de ser Senador, e não te fizeste monge." },
  { category: "patristic", author: "Um monge do deserto", source: "Apotegmas", text: "Antes dele, eu morri para o mundo; um morto não recebe heranía de um vivo." },
  { category: "patristic", author: "Abba Pimen", source: "Apotegmas", text: "Se alguém ouvir uma palavra ofensiva e ao invês de respondÉ-la com igual ofensa, controlar seus sentimentos e permanecer em silÉncio, ou se alguém for trapaceado e suportar isso sem vingar-se, ele estará dando sua vida ao próximo." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O homem não pode expulsar as lembranías apaixonadas se não presta ateníão  concupiscÉncia e  cólera." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A soberba ê um tumor da alma cheio de pus. Se maduro, explodirá, emanando terróvel fedor." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A abundéncia de frutos dobra os ramos da árvore; a abundéncia de virtudes humilha a mente do homem." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O monge soberbo ê como uma árvore sem raózes e não suporta o ómpeto do vento." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A vanglória ê uma paixão irracional que facilmente se enraóza em todas as obras virtuosas." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A hera adere  árvore e, quando chega ao ponto mais alto, seca-lhe a raiz; assim, a vanglória se origina nas virtudes e não se afasta enquanto não lhes tiver consumido as forías." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O aborrecimento ê a debilidade da alma que irrompe quando não se vive segundo a natureza, nem se enfrenta nobremente a tentaíão." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "Uma árvore transplantada não frutifica e o monge vagabundo não produz fruto de virtude." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A tristeza ê a boca do leão e facilmente devora aquele que se entristece." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O monge triste não conhece a alegria espiritual, como aquele acometido por forte febre não reconhece o sabor do mel." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A ira ê uma paixão furiosa que, com frequÉncia, faz perder o juízo queles que tÉm o conhecimento." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O monge magnénimo ê uma fonte tranquila, uma bebida agradável oferecida a todos." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A avareza ê a raiz de todos os males e nutre, como arbustos malignos, as demais paixões." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O monge que não possui nada ê um viajante ágil que encontra refœgio em todos os lados." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O desejo de riquezas do ávaro jamais se sacia: ele o duplica e deseja quadruplicá-los, atê que a morte venha pùr fim a tal pretensão." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A moderaíão gera a regra, enquanto que a gula ê a mãe do desenfreio." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O olhar para uma mulher ê semelhante a um dardo venenoso: fere a alma, nos injeta veneno e, quanto mais perdura, tanto mais espalha a infecíão." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "Permanece invulnerável s flechas inimigas aquele que ama a paz interior e a tranquilidade de recolhimento." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "Uma coluna se apoia sobre uma base, e a paixão da luxœria tem sua base na saciez." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "Apenas quando a recordaíão da mulher surgir em ti separada da paixão, então poderás considerar-te próximo dos confins da sabedoria." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A origem do fruto ê a flor, e a origem da disciplina espiritual ê a moderaíão." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "Quem domina o próprio estùmago, diminui as paixões." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "Um ventre indigente prepara para uma oraíão vigilante; ao contrário, um ventre bem cheio convida para um longo sono." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "A oraíão daquele que jejua ê como um pintinho voando mais alto que uma águia." },
  { category: "patristic", author: "Evágrio Pùntico", source: "Apotegmas", text: "O desejo de comida ê como o fogo que sempre envolve e sempre se inflama." },
  { category: "patristic", author: "Abba Evagro", source: "Apotegmas", text: "Tomar regularmente alimento não-cozido junto com a castidade conduz o monge num átimo ao porto da impassibilidade Ñ total domónio das paixões." },
  { category: "patristic", author: "Abba Isaóas", source: "Apotegmas", text: "O que ê preferóvel dentre tudo? Fazer a vontade de Deus e seguir o caminho da salvaíão." },
  { category: "patristic", author: "Abba Zózimo", source: "Apotegmas", text: "Haverá algum solitário no mundo que possa ensinar-me algo de novo, ou mostrar-me algo que eu ainda não tenha realizado por minhas próprias aíões nesta santa maneira de viver?" },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Um homem que está irado, mesmo que ressuscitasse os mortos, não ê aceitável a Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Sem a observância vigilante, o homem não progride em uma œnica virtude." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Nunca fui dormir guardando uma queixa contra alguém, nem permiti que ninguêm fosse dormir com uma queixa contra mim." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Se eu pudesse encontrar um leproso para dar-lhe o meu corpo e tomar o dele, eu o faria com alegria; pois isso ê a caridade perfeita." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: " perigoso para qualquer um tentar ensinar se não tiver sido primeiro treinado na vida prática." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Aqueles que se aproximam de Deus tÉm, a princópio, uma luta e um trabalho árduo; mas depois, uma alegria indizóvel.  como acender um fogo: primeiro há fumaía e as lágrimas correm, mas depois se obtêm o resultado desejado." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Existem muitos que vivem nas montanhas e se comportam como se estivessem na cidade, e eles estão perdendo seu tempo.  possóvel ser solitário na mente enquanto se vive em uma multidão, e ê possóvel para aquele que vive na solidão viver na multidão de seus próprios pensamentos." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Assim como um tesouro, quando exposto, ê rapidamente gasto, assim também qualquer virtude que se torna conhecida e divulgada ê dissipada." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Muitas vezes o diabo traz um jejum excessivo para enfraquecer o corpo e tornar a alma inœtil para a oraíão." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Assim como os marinheiros navegam pelas estrelas, os monges devem navegar pela vida dos anciãos." },
  { category: "patristic", author: "Amma Sara", source: "Apotegmas", text: "Se eu rogasse a Deus que todos os homens fossem edificados por mim, eu me encontraria fazendo penitÉncia  porta de cada um; mas eu rogarei que meu coraíão seja puro com todos." },
  { category: "patristic", author: "Amma Sara", source: "Apotegmas", text: "Eu coloco meu pê na escada para subir, e coloco a morte diante de meus olhos antes de subir." },
  { category: "patristic", author: "Amma Sara", source: "Apotegmas", text: "Não temo a Deus, mas O amo; pois o amor lanía fora o medo." },
  { category: "patristic", author: "Amma Teodora", source: "Apotegmas", text: "Um monge deve estar sempre pronto, como se a morte estivesse  porta, para não ser pego desprevenido." },
  { category: "patristic", author: "Amma Teodora", source: "Apotegmas", text: "Assim como o melaío atrai moscas, os prazeres deste mundo atraem os demùnios." },
  { category: "patristic", author: "Abba Benjamin", source: "Apotegmas", text: "O monge deve ser como os Querubins: todo olho." },
  { category: "patristic", author: "Abba Benjamin", source: "Apotegmas", text: "Se vocÉ guardar o silÉncio, encontrará descanso em qualquer lugar que morar." },
  { category: "patristic", author: "Abba Benjamin", source: "Apotegmas", text: "Não reclame da comida, pois a murmuraíão afasta a graía da mesa." },
  { category: "patristic", author: "Abba Gelásio", source: "Apotegmas", text: "Não procure saber o que o seu vizinho está fazendo na sua cela; procure saber o que está acontecendo dentro da sua." },
  { category: "patristic", author: "Abba Hiperóquio", source: "Apotegmas", text: "Aquele que não domina sua lóngua em tempos de raiva, nunca dominará suas paixões." },
  { category: "patristic", author: "Abba Hiperóquio", source: "Apotegmas", text: " melhor comer carne e beber vinho do que devorar a carne de seus irmãos atravês da calœnia." },
  { category: "patristic", author: "Abba Hiperóquio", source: "Apotegmas", text: "A casa do tesouro do monge ê a pobreza voluntária. Portanto, meu irmão, ajunta o teu tesouro no cêu; pois lá habitam as eras de quietude sem fim." },
  { category: "patristic", author: "Abba Hiperóquio", source: "Apotegmas", text: "Foi atravês da murnuraíão que a serpente expulsou Eva do Paraóso; assim, aquele que fala contra o seu próximo será como a serpente, pois corrompe a alma de quem o ouve e não salva a sua própria alma." },
  { category: "patristic", author: "Abba Nóstero", source: "Apotegmas", text: "Se vocÉ quer que Deus ouía sua oraíão rapidamente, antes de rezar por qualquer outra coisa, deve rezar por seus inimigos." },
  { category: "patristic", author: "Abba Nóstero", source: "Apotegmas", text: "A obediÉncia ê preferóvel ao sacrifócio." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Se vocÉ for para o deserto, não confie em suas boas obras, mas entregue-se inteiramente  misericórdia de Deus." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "O monge deve usar o tipo de roupa que, se ele a jogasse para fora da cela, ninguêm a pegaria." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "Se ele não for edificado pelo meu silÉncio, não será edificado pela minha fala." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "A obediÉncia ê maior do que a virtude de todos vós; pois cada um de vós obteve qualquer virtude que possui por sua própria vontade, enquanto o obediente suspende sua própria vontade e cumpre a vontade de outra pessoa." },
  { category: "patristic", author: "Abba Pambo", source: "Apotegmas", text: "TrÉs anos eu levei para aprender a não falar sem necessidade; e vocÉ quer aprender a teologia em um dia?" },
  { category: "patristic", author: "Abba Pafnœcio", source: "Apotegmas", text: "Tudo o que ê feito com excesso, mesmo o jejum, ê prejudicial ao monge." },
  { category: "patristic", author: "Abba Pafnœcio", source: "Apotegmas", text: "Aquele que julga seu irmão assume o papel de Deus, o œnico que tem o direito de julgar." },
  { category: "patristic", author: "Abba Pafnœcio", source: "Apotegmas", text: "Se vocÉ vir um irmão pecando, cubra-o com seu manto em vez de espalhar a notócia." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A humildade ê a œnica coisa que o diabo não pode imitar." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Se vocÉ cair, levante-se novamente; se cair de novo, levante-se de novo." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "DÉ a Deus o que ê de Deus e ao mundo o que ê do mundo, mas guarde seu coraíão apenas para Ele." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Atê o fim da minha vida, não deixarei de pedir a Deus que perdoe meus pecados, pois nunca sei se fiz algo que O agradou." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Tende coragem; por trinta anos não tenho mais implorado a Deus sobre o pecado, mas digo isto quando rezo: Senhor Jesus Cristo, protege-me da minha lóngua, pois mesmo atê agora caóo em pecado por causa dela." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "O arrependimento ê o trabalho de cada dia, atê o œltimo suspiro." },
  { category: "patristic", author: "Abba Sisoes o Tebano", source: "Apotegmas", text: "Não descansarei atê conseguir minha vinganía contra ele, disse o irmão. O ancião então orou: î Deus, não precisamos mais que cuides de nós, pois estamos colhendo nossa própria vinganía. O irmão caiu aos pês do ancião e disse: Não terei mais problemas com meu irmão. Perdoa-me!" },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Muitos buscam o repouso antes que Deus o dÉ a eles." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Se vocÉ não for como um cadáver na sepultura, não poderá ser um monge." },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "A maior virtude ê a discriíão, pois ela governa todas as outras." },
  { category: "patristic", author: "Abba Teodoro de Pherme", source: "Apotegmas", text: "Se vocÉ não está em paz sozinho nem com os outros, por que se tornou monge? Não foi para sofrer provaíões?" },
  { category: "patristic", author: "Abba Teodoro", source: "Apotegmas", text: "Ainda não encontraste o teu navio, nem puseste nele a tua bagagem, nem comeíaste a navegar, e já estás na cidade para onde planeaste ir. Quando tiveres primeiro trabalhado naquilo de que falas, então fala a partir da própria coisa." },
  { category: "patristic", author: "Abba Zenão", source: "Apotegmas", text: "Se alguém quer que Deus ouía sua oraíão rapidamente, antes de rezar por qualquer outra coisa, deve rezar por seus inimigos." },
  { category: "patristic", author: "Abba Zenão", source: "Apotegmas", text: "Não viva em um lugar onde vocÉ seja famoso ou onde tenha grandes posses." },
  { category: "patristic", author: "Abba Zenão", source: "Apotegmas", text: "Aquele que carrega uma ofensa no coraíão ê como alguém que guarda uma cobra no peito." },
  { category: "patristic", author: "Abba Elias", source: "Apotegmas", text: "Se o espórito não cantar com o corpo, o trabalho ê em vão." },
  { category: "patristic", author: "Abba Elias", source: "Apotegmas", text: "Eu temo trÉs coisas: o momento em que à minha alma deixará o corpo, o momento em que comparecerá diante de Deus e o momento em que a sentenía será dada." },
  { category: "patristic", author: "Abba Elias", source: "Apotegmas", text: " necessário ter grande empenho, e quem não o faz não pode chegar ao seu Deus. Pois Ele mesmo foi crucificado por nossa causa." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "Quanto mais o corpo engorda, mais a alma definha." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "Aquele que quer se aproximar de Deus deve primeiro se afastar dos homens em seu coraíão." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O amor ao dinheiro ê o veneno da vida monástica." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "O corpo prospera na medida em que a alma ê enfraquecida, e a alma prospera na medida em que o corpo ê enfraquecido." },
  { category: "patristic", author: "Abba Daniel", source: "Apotegmas", text: "Quem nos separará daqui em diante de Deus? Deus está na cela e, por outro lado, Ele está fora também." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Aquele que ê honrado pelos homens não pode escapar do orgulho." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Em todos os seus trabalhos, a humildade deve vir primeiro." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Nunca diga em seu coraíão que vocÉ ê melhor que seu irmão." },
  { category: "patristic", author: "Abba Or", source: "Apotegmas", text: "Nunca traga uma máxima estranha para esta cela." },
  { category: "patristic", author: "Abba Lot", source: "Apotegmas", text: "VocÉ não pode ser monge se não se tornar como um fogo ardente." },
  { category: "patristic", author: "Abba Lot", source: "Apotegmas", text: "Vá, sente-se em sua cela e ela lhe ensinará tudo." },
  { category: "patristic", author: "Abba Nabrão", source: "Apotegmas", text: "O trabalho manual ê necessário para afastar a preguiía, que ê a mãe de todos os vócios." },
  { category: "patristic", author: "Abba Isidoro o Presbótero", source: "Apotegmas", text: "A razão pela qual os demùnios tÉm tanto medo de mim ê porque tenho praticado o ascetismo desde o dia em que me tornei monge, e não permiti que a raiva chegasse aos meus lábios." },
  { category: "patristic", author: "Abba Isidoro o Presbótero", source: "Apotegmas", text: "Por quarenta anos fui tentado a pecar em pensamento, mas nunca consenti nem com a cobia nem com a raiva." },
  { category: "patristic", author: "Abba Isidoro o Presbótero", source: "Apotegmas", text: "Mesmo que eu fosse queimado e minhas cinzas jogadas ao vento, não me permitiria nenhum relaxamento, porque o Filho de Deus veio aqui por nossa causa." },
  { category: "patristic", author: "Abba Isidoro o Presbótero", source: "Apotegmas", text: "Quando ê que me comparei com Abba Antùnio, com Abba Pambo, ou com os outros Padres que agradaram a Deus?" },
  { category: "patristic", author: "Abba Isidoro o Presbótero", source: "Apotegmas", text: "Mesmo que eu seja enviado ao inferno, eu os encontrarei abaixo de mim Ñ dizia sobre os demùnios que o ameaíavam." },
  { category: "patristic", author: "Abba Isidoro o Presbótero", source: "Apotegmas", text: "Um dia fui ao mercado vender alguns pequenos bens; quando vi a raiva se aproximando de mim, deixei as coisas e fugi." },
  { category: "patristic", author: "Abba Isidoro o Presbótero", source: "Apotegmas", text: "Verdadeiramente, irmãos, não vi o rosto de ninguêm na cidade, exceto o do arcebispo; o pensamento de olhar para qualquer pessoa não levou a melhor sobre mim." },
  { category: "patristic", author: "Abba Serapião", source: "Apotegmas", text: "Quando os soldados do imperador estão em posiíão de sentido, eles não podem olhar para a direita ou para a esquerda; ê o mesmo para o homem que está diante de Deus." },
  { category: "patristic", author: "Abba Serapião", source: "Apotegmas", text: "Se vocÉ quer ser humilde, aprenda a suportar generosamente o que os outros injustamente infligem a vocÉ e não abrigue palavras vazias em seu coraíão." },
  { category: "patristic", author: "Abba Serapião", source: "Apotegmas", text: "Abba Serapião converteu uma cortesã passando a noite em oraíão por ela; ela ficou tão tocada pela compuníão que se tornou monja e viveu o resto de sua vida em rigorosa penitÉncia." },
  { category: "patristic", author: "Abba Benjamin", source: "Apotegmas", text: "Pela graía de Deus, desde que vesti este hábito, nunca disse uma palavra da qual tivesse que me arrepender depois." },
  { category: "patristic", author: "Abba Efraim", source: "Apotegmas", text: "Quando era crianía, teve um sonho: uma videira brotou de sua lóngua, cresceu e encheu todo o espaío sob o cêu, era muito frutófera. Todas as aves do cêu comiam do fruto da videira e, enquanto comiam, o fruto se proliferava." },
  { category: "patristic", author: "Abba Epifénio", source: "Apotegmas", text: "A aquisiíão de livros cristãos ê necessária para aqueles que podem usá-los. Pois a mera visão desses livros nos torna menos inclinados ao pecado e nos incita a crer mais firmemente na retidão." },
  { category: "patristic", author: "Abba Epifénio", source: "Apotegmas", text: "A leitura das Escrituras ê uma grande salvaguarda contra o pecado." },
  { category: "patristic", author: "Abba Matoês", source: "Apotegmas", text: "Quanto mais um homem se aproxima de Deus, maior pecador ele se vÉ. Pois o profeta Isaóas viu a Deus e disse que era impuro e estava perdido." },
  { category: "patristic", author: "Abba Gerùncio de Petra", source: "Apotegmas", text: "Muitos são aqueles que, tentados pelos deleites da carne, entregam-se  imoralidade mental sem qualquer contato fósico. Enquanto preservam sua virgindade fósica, entregam-se  imoralidade espiritual.  bom fazer o que está escrito: Cada um guarde o seu próprio coraíão com toda a vigiléncia." },
  { category: "patristic", author: "Abba Paulo (via João discópulo)", source: "Apotegmas", text: "Abba Paulo disse a João: Vá e tire o esterco da cova do leão. João obedeceu e foi; a leõa saiu para saltar sobre ele, mas ele, obedecendo  palavra do ancião, investiu contra ela para pegá-la. A leõa fugiu e ele a perseguiu dizendo: Espere, meu abade me disse para amarrá-la. E a amarrou." },
  { category: "patristic", author: "Abba Nilo", source: "Apotegmas", text: "Invulnerável s flechas do inimigo ê aquele que ama o silÉncio; mas aquele que se mistura com a multidão frequentemente recebe feridas." },
  { category: "patristic", author: "Abba Paulo", source: "Apotegmas", text: "Se alguém obteve a pureza, tudo lhe está em submissão, como estava para Adão, quando ele estava no paraóso antes de transgredir o mandamento." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Se alguém guarda a memória de quem o afligiu, deve lembrar-se dessa pessoa como um mêdico enviado por Cristo e deve considerá-la um benfeitor. O próprio fato de vocÉ ser ferido por tais coisas indica que sua alma está doente; vocÉ deveria ficar feliz por ter tal irmão, pois ê atravês dele que se torna consciente de sua própria doenía." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Um ancião morava no deserto e sua cela ficava longe da água, cerca de sete milhas. Certa vez desanimou e quis se mudar para perto da água. Então viu alguém seguindo-o e contando seus passos. Perguntou: Quem ê vocÉ? O outro respondeu: Sou o anjo do Senhor, e fui enviado para contar teus passos e te dar a tua recompensa. O coraíão do ancião se fortaleceu e colocou sua cela ainda mais longe da água." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Senta-te em tua cela e faz o que puderes, e não te perturbes; pois o pouco que fazes agora ê como quando Antùnio fez grandes e muitas coisas no deserto. Quem se senta em sua cela por amor de Deus e guarda sua consciência será encontrado no lugar de Antùnio." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Trabalhe no labor manual o máximo que puder, a fim de prover para aquele que tem necessidade, pois está escrito: Os pecados são purificados pela esmola e por atos de fê." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Havia uma vez ladrões que entraram na cela de um ancião e disseram: Tudo o que tens em tua cela, viemos levar. E ele disse: Tomai tudo o que vedes, meus filhos. Eles pegaram tudo, esquecendo apenas uma pequena bolsa escondida. O ancião correu atrás deles gritando: Meus filhos, esquecestes isto: tomai-o. E eles, maravilhados com a paciÉncia do ancião, trouxeram tudo de volta e fizeram penitÉncia." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Não desanime se a doenía fósica sobrevier a vocÉ. Quem ê vocÉ para se ofender se o seu Senhor e mestre deseja que vocÉ seja afligido no corpo? Resigne-se e rogue a Deus que lhe conceda o que for apropriado, segundo a Sua Vontade; permaneía pacientemente em sua cela, alimentando-se de caridade." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Diziam sobre Lázaro, o pobre: A œnica coisa que encontramos nele ê que ele nunca se queixou contra o Senhor por não lhe mostrar misericórdia, mas suportou sua afliíão graciosamente.  por isso que Deus o aceitou." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "TrÉs monges foram  beira do mar: dois tomaram asas de fogo e voaram para a outra margem; o terceiro permaneceu, chorando, e depois foi-lhe dado asas fracas, chegando ao outro lado com grande dificuldade. Assim ê com esta geraíão: se receber asas, elas ainda não serão de fogo, mas fracas." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Esta ê a vida do monge: trabalho, obediÉncia, meditaíão, não julgar, não difamar, não gritar. Não ter nada a ver com o que ê injusto, não ver o mal com os próprios olhos, não ser um intrometido, não ouvir os assuntos alheios; dar em vez de tirar com as mãos." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Rogue a Deus para lhe dar tristeza no coraíão e humildade. Esteja sempre atento aos seus pecados e não julgue os outros; pelo contrário, coloque-se abaixo de todos os outros. Não mantenha amizade com uma mulher, com uma crianía ou com herêticos." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Assim ê com quem permanece entre os homens: por causa da turbulÉncia, ele não vÉ seus pecados; mas quando está em silÉncio, sobretudo na solidão, então reconhece suas próprias falhas. Como a água turva que ao assentar reflete os rostos como num espelho." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Nunca desejei um trabalho que me beneficiasse s custas de meu irmão, na esperanía de que o trabalho de meu irmão fosse um trabalho que desse frutos." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Seja como alguém que passa por uma casa de pasto e sente o cheiro da sopa ou de alguma carne assada. Aquele que desejava comer, entrava e comia; aquele que não queria, simplesmente sentia o cheiro ao passar e depois ia embora. Assim ê com as tentaíões: sacuda de si o fedor e levante-se e ore, dizendo: Filho de Deus, ajuda-me." },
  { category: "patristic", author: "Um Irmão (Anùnimo)", source: "Apotegmas", text: "Ao diabo disfaríado de anjo que disse ser Gabriel: Certifique-se de que não foi enviado a outra pessoa, pois não sou digno de ver um anjo." },
  { category: "patristic", author: "Um Irmão (Anùnimo)", source: "Apotegmas", text: "Um irmão estava colocando alías nos cestos quando ouviu o vizinho que não tinha alías para os seus cestinhos. Foi, soltou as alías dos seus próprios cestos e as trouxe ao vizinho, dizendo: Tenho estas sobrando; pegue-as. Ele melhorou o trabalho do irmão em detrimento do seu próprio." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Uma pergunta de um irmão: 'Como posso encontrar Deus?'. Resposta do ancião: Atravês do jejum, da vigólia e, acima de tudo, da caridade." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "A cela do monge ê a fornalha da Babilùnia onde os trÉs jovens encontraram o Filho de Deus." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Se vocÉ não tem paz dentro de si, não a encontrará no deserto." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "O silÉncio ê o porteiro da oraíão." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Não julgue ninguêm, pois no momento em que vocÉ julga, vocÉ cai no mesmo pecado." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Um ancião foi questionado: O que ê orar sem cessar? E ele respondeu:  a petiíão enviada a Deus do próprio fundamento do coraíão solicitando o que ê apropriado. Pois não ê apenas quando nos levantamos para a oraíão que estamos orando; a verdadeira oraíão ê quando vocÉ pode orar o tempo todo dentro de si mesmo." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Ao cair da morte: Vi os anjos virem buscar-me. Pedi para ser deixado um pouco mais para me arrepender. Os Padres disseram: Não tens necessidade de arrependimento. Mas ele disse: Verdadeiramente não sei se sequer comecei o inócio do arrependimento." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Um navio naufragou no mar e perdeu toda a carga que carregava, e com muito esforío o navio vazio foi finalmente trazido  terra.  vosso desejo afundar em terra o navio que chegou seguro do mar? Ñ ao receber um irmão expulso de um mosteiro." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Vi todas as armadilhas do inimigo estendidas sobre a terra, e gemendo perguntei: Quem escapará a elas? Ouvi então uma voz que me dizia: A humildade." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Sem tentaíões, ninguêm pode ser salvo. Quem retirar as tentaíões, não achará ninguêm salvo." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Como os peixes que se demoram na terra seca morrem, assim também os monges que permanecem fora da cela, ou vivem com seculares, relaxam a tensão da vida recolhida." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "Se um rei quisesse tomar posse da cidade de um inimigo, ele comeíaria cortando a água e a comida; e assim seus inimigos, morrendo de fome, se submeteriam.  o mesmo com as paixões da carne: se um homem pratica o jejum e a fome, os inimigos de sua alma tornam-se fracos." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "Nós colocamos o fardo leve de lado, que ê a auto-acusaíão, e nos carregamos com um pesado, que ê a auto-justificaíão." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "A humildade e o temor de Deus estão acima de todas as virtudes." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "Vá, rogue a Deus para despertar a guerra, para que vocÉ recupere a afliíão e a humildade que costumava ter, pois ê pela guerra que a alma progride." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Veja, Abba Moisês, agora vocÉ está inteiramente branco ao ser ordenado. Ele respondeu:  verdade por fora; mas o que dizer dAquele que vÉ o interior?" },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O homem que foge e vive na solidão ê como um cacho de uvas amadurecido pelo sol, mas aquele que permanece entre os homens ê como uma uva verde." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Se guardarmos os mandamentos de nossos Padres, eu responderei por isso em nome de Deus que os bárbaros não virão aqui. Mas se não guardarmos os mandamentos de Deus, este lugar será devastado." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Quanto a mim, tenho esperado por este dia por muitos anos, para que a palavra do Senhor Cristo se cumpra: Todos os que laníarem mão da espada,  espada morrerão." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Se um homem não pensa em seu coraíão que já está há trÉs dias morto e no tœmulo, ele não pode alcaníar este estado de morte para o próximo." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Meus pecados correm atrás de mim e eu não os vejo, e hoje venho julgar os erros de outro Ñ disse ao carregar um jarro furado com areia para mostrar como suas faltas sempre escapam pelas costas." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Tratado com desprezo por ser negro em um conselho em Cêtis, Moisês manteve silÉncio absoluto, dizendo depois que, embora estivesse triste, preferiu calar-se." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "ArsÉnio recusou uma heranía dizendo: Eu morri antes dele; ele morreu há pouco. Um morto não herda de um vivo." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "ArsÉnio, porque te retiraste do mundo? De ter falado, arrependi-me muitas vezes; de me ter calado, nunca." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "No fim do sábado, estendia as mãos ao cêu em oraíão atê que de novo brilhasse o sol sobre a sua face. Assim preparava a glória do domingo." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Dizia o Abade Poòmen que ArsÉnio chorou a si mesmo neste mundo para não chorar no outro; pois ê impossóvel não chorar, voluntária ou obrigatoriamente." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Ao ver um menino que dizia: certo homem rico gosta de mim e eu o odeio; um pobre me odeia e eu o amo, Macário admirou-se e disse aos irmãos: Em verdade, Nosso Senhor ê rico e nos ama, enquanto nós não o queremos ouvir. Ao contrário, nosso inimigo, o demùnio, ê pobre e nos odeia, enquanto nós amamos a sua impureza." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Eis o que eu sei: durante doze anos servi ao Senhor para que me concedesse esta graía de receber a todos com simplicidade; e vocÉs todos me aconselham a rejeitá-la?" },
  { category: "patristic", author: "Abba Poemen (Pastor)", source: "Apotegmas", text: " pela guerra que a alma progride." },
  { category: "patristic", author: "Abba Poemen (Pastor)", source: "Apotegmas", text: "Se vocÉ quer encontrar descanso, diga: Quem sou eu? e não julgue ninguêm." },
  { category: "patristic", author: "Abba Poemen (Pastor)", source: "Apotegmas", text: "Na hora em que encobrirmos os pecados de nosso irmão, Deus cobrirá os nossos; e na hora em que trairmos as vergonhas de nosso irmão, da mesma forma Deus trairá as nossas." },
  { category: "patristic", author: "Abba Poemen (Pastor)", source: "Apotegmas", text: "Se um homem pecou e não o nega, mas diz eu pequei, não o repreenda, pois vocÉ quebrará o propósito de seu coraíão. Em vez disso, diga a ele: Não fique triste, meu irmão, mas vigie-se daqui em diante, e vocÉ despertará o coraíão dele para o arrependimento." },
  { category: "patristic", author: "Abba Poemen (Pastor)", source: "Apotegmas", text: "A marca do monge torna-se aparente nas tentaíões." },
  { category: "patristic", author: "Um Ancião (Anùnimo)", source: "Apotegmas", text: "Um ancião foi questionado sobre a oraíão contónua e respondeu:  a petiíão enviada a Deus do próprio fundamento do coraíão. A verdadeira oraíão ê quando vocÉ pode orar o tempo todo dentro de si mesmo." },
  { category: "patristic", author: "Um Grande Ancião", source: "Apotegmas", text: "Tu dás consentimento  tua imaginaíão. Deves aprender como resistir a tais pensamentos: não te inclines a eles em tua mente, mas prostra-te diante de Deus em oraíão para encontrar a paz contra a tentaíão." },
  { category: "patristic", author: "Abba Efraim o Sório", source: "Apotegmas", text: "A palavra do próximo ê como fogo para ti Ñ pode purificar ou queimar.  preciso discernimento." },
  { category: "patristic", author: "Abba Isidoro", source: "Apotegmas", text: "O amor tem de ser preferido a tudo: ele une todas as coisas e todas as coisas une a Deus." },
  { category: "patristic", author: "Abba Pafsio (Pafsios)", source: "Apotegmas", text: "Quando vejo que um irmão comete um erro, considero que ê culpa minha, pois não o construó suficientemente pelo exemplo." },
  { category: "patristic", author: "Abba Silvano", source: "Apotegmas", text: "O ancião dizia ao discópulo: Faz o que queres, mas não abandones as horas da oraíão; pois quem abandona as horas, perde a própria vida." },
  { category: "patristic", author: "Abba Longino", source: "Apotegmas", text: "Não há virtude maior que a não ter ambiíão de nenhuma coisa terrena; e não há vócio maior do que amar as riquezas." },
  { category: "patristic", author: "Abba Longino", source: "Apotegmas", text: "TrÉs obras corporais são necessárias para o monge: o jejum, a vigólia e o trabalho das mãos." },
  { category: "patristic", author: "Abba Longino", source: "Apotegmas", text: "Se alguém faz pœblica sua ascese, já recebeu o seu prÉmio." },
  { category: "patristic", author: "Abba Lot (Padre)", source: "Apotegmas", text: "Não nos preocupemos tanto com o exercócio das virtudes, mas com a guarda do coraíão; pois ê dela que os exercócios brotam." },
  { category: "patristic", author: "Abba João Ancião de Tebas", source: "Apotegmas", text: "Acima de tudo, o monge necessita da humildade, pois esta ê a primeira virtude que Nosso Senhor ensinou quando disse: Bem-aventurados os pobres de espórito." },
  { category: "patristic", author: "Abba Cassiano (Colachões)", source: "Apotegmas", text: "A perfeiíão do coraíão não consiste em macerar o corpo, mas em desprender-se de todo vócio e amor ao próprio prazer." },
  { category: "patristic", author: "Abba Cassiano (Colachões)", source: "Apotegmas", text: "O fim do monge ê o reino de Deus; o objetivo proximal ê a pureza de coraíão, sem a qual ninguêm pode alcaníar aquele fim." },
  { category: "patristic", author: "Abba Cassiano (Colachões)", source: "Apotegmas", text: "Toda aíão, todo exercócio, toda ascese devem ser julgados segundo esta œnica norma: serve  pureza do coraíão ou não?" },
  { category: "patristic", author: "Abba Cassiano (Colachões)", source: "Apotegmas", text: "Ao jovem que perguntou sobre o jejum, respondeu: O jejum ê como um instrumento; tem valor quando usado com discriíão. Usado fora da medida, ê tão prejudicial quanto o excesso de comida." },
  { category: "patristic", author: "Abba Cassiano (Colachões)", source: "Apotegmas", text: "A acódia ê o mais pesado dos demùnios, o mais opressivo dos monges; ataca ao redor da hora sexta e não dá trêgua  alma tão logo os exercócios se tornem monótonos." },
  { category: "patristic", author: "Abba Cassiano (Colachões)", source: "Apotegmas", text: "A alma não pode permanecer no mesmo estado espiritual. Ou progride ou regride; portanto o monge deve sempre aspirar mais alto." },
  { category: "patristic", author: "Abba Cassiano (Colachões)", source: "Apotegmas", text: "Quem não aprendeu a dominar sua lóngua, como pode aprender a dominar suas paixões? O silÉncio ê a escola de todas as virtudes." },
  { category: "patristic", author: "Abba Or (Pai Or)", source: "Apotegmas", text: "Conserva-te em humildade. Quando houver discussões entre os irmãos, fica em silÉncio e reza. Agirias como Salomão, que era mais sábio que todos, mas permanecia em silÉncio diante dos fortes." },
  { category: "patristic", author: "Abba Nóstero (Nesteros)", source: "Apotegmas", text: "Perguntas-me o que ê que devo fazer. Devo dizer-te que a primeira coisa ê guardar tua mente em paz com todos. A segunda ê não permanecer ocioso. A terceira ê o pensamento constante de Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Falei uma vez, não voltarei a falar segunda vez Ñ disse ao ser corrigido pelo presbótero por ter tomado a palavra numa assemblêia sem ser interrogado." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Ele ê do alto e fala de coisas celestiais, enquanto eu sou de baixo e falo de coisas terrestres. Se me tivesse falado das paixões da alma, eu lhe teria respondido Ñ ao estrangeiro que veio tratando só de coisas celestes." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Se o homem tem em si mesmo o temor de Deus, o inimigo não pode prejudicá-lo." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Não habites em lugar onde vocÉ vÉ que alguns tÉm inveja de ti; do contrário, não progredirás." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O homem que conhece a si mesmo ê maior que aquele que ressuscita um morto." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Aquele que vence suas próprias paixões ê maior do que aquele que vence um exêrcito." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Ser humilhado ê melhor do que ser honrado, pois a humilhaíão produz mansidão e a honra produz arrogéncia." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Se a tribulaíão te chega de fora, ela te prejudica por algum tempo; se a tribulaíão vem de dentro, ela te consome completamente." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Enquanto o pote está no fogo, não se afastam as moscas nem os mosquitos. Assim também enquanto o monge permanece em aíões espirituais, o inimigo não pode lhe fazer nada." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O espadeiro que conhece seu ofócio pode transformar uma espada em foice; assim também pode transformar uma foice em espada. Seja discópulo do que serve ao momento." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A vida espiritual do monge ê composta de trÉs elementos essenciais: temor de Deus, humildade e abstinÉncia." },
  { category: "patristic", author: "Abba Macário de Alexandria", source: "Apotegmas", text: "Macário de Alexandria disse que, por cada coisa que cometemos mal durante o dia, durante a noite deviaóamos fazer penitÉncia dupla; e por cada coisa boa que fazemos durante a noite, devemos dobrar o esforío durante o dia." },
  { category: "patristic", author: "Abba Macário de Alexandria", source: "Apotegmas", text: "Uma vez por semana Macário de Alexandria comia apenas pão seco, sal e água, e isso era chamado a dieta de penitÉncia; assim ele dominou seu corpo para que não dominasse sua alma." },
  { category: "patristic", author: "Abba Macário de Alexandria", source: "Apotegmas", text: "Se os pensamentos me atormentam, digo: Olha para o que vocÉ ê. Isso me basta para envergùnha-los, pois nada sou." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando a luta do corpo se torna forte contra vocÉ, arme-se com a compuníão e a humildade, e o Senhor logo acorrerá em seu auxólio." },
  { category: "patristic", author: "Abba Moisês (Padres de Scete)", source: "Apotegmas", text: "A compuníão ê a chave que abre a porta de Deus." },
  { category: "patristic", author: "Abba Moisês (Padres de Scete)", source: "Apotegmas", text: "A guarda dos mandamentos ê para o monge como o leite para a crianía: sem ela, não pode crescer." },
  { category: "patristic", author: "Abba Moisês (Padres de Scete)", source: "Apotegmas", text: "A oraíão sem silÉncio ê como semear em terra dura; as sementes caem mas não germinam." },
  { category: "patristic", author: "Abba Pacomóus", source: "Apotegmas", text: "Não seja vingativo; não seja maledicente; não seja ciumento; não seja falso; não seja adulador; não seja soberbo. Observe estas seis coisas e viverá no Senhor." },
  { category: "patristic", author: "Abba Pacomóus", source: "Apotegmas", text: "A caridade ê a mãe de todas as virtudes; sem ela, nenhuma virtude pode subsistir. Como o corpo sem alma, a virtude sem amor ê cadáver." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando um irmão caiu, um anjo me mostrou que cada queda pode ser remediada pelo arrependimento, se o coraíão não endurecer." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Não posso ser um monge, mas vi monges; e eles me ensinaram que a humildade ê o fundamento de todas as virtudes." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Perguntaram a um ancião: Por que temos tantos pensamentos maus? Ele respondeu: Porque comemos muito e dormimos muito; isso dá ao inimigo espaío para entrar." },
  { category: "patristic", author: "Abba Silvano (de Scete)", source: "Apotegmas", text: "Não ensineis antes de serem interrogados; e, quando interrogados, respondei o necessário e não mais. A palavra demais ê como prato demais: embrulha o estùmago." },
  { category: "patristic", author: "Abba Silvano (de Scete)", source: "Apotegmas", text: "O bom monge ê aquele cujo coraíão está em Deus quando fala, quando trabalha e quando come; de tal modo que não há nele separaíão entre o exterior e o interior." },
  { category: "patristic", author: "Abba Sisoes (Pastor)", source: "Apotegmas", text: "Aquele que sempre se acusa a si mesmo por tudo que lhe acontece, esse já tem o inócio da humildade." },
  { category: "patristic", author: "Abba Serapião (de Thmuis)", source: "Apotegmas", text: "Não pode ser unão com Deus quem não está unido ao seu próximo pelo amor; pois Deus ê amor e aquele que permanece no amor permanece em Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Vi monges que fizeram grandes coisas exteriores, mas dentro era vazio; e vi monges que fizeram coisas pequenas, mas dentro havia um paóz celeste." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Não há trabalho maior que o de guardar a mente limpa s vêsperas da morte." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Procurei um homem sem falha e não o encontrei; procurei um homem humilde e o encontrei em todos os lugares." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O monge que ê louvado pelos homens e lão se humilha, já perdeu o fruto de suas vigólias." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "A alma que está cheia de amor não tem lugar para o julgamento dos outros; cada vez que ela olha para os outros, vÉ apenas a própria misêria." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Se Deus me pede conta de cada palavra ociosa, com mais razão me pedirá conta de cada silÉncio ocioso Ñ quando deveria ter falado e não falei." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Quem quer que seja, quando encontrar alguém que peca, deve rezar por ele e não o julgar; pois Deus ê o œnico juiz." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Se o monge não tem em seu coraíão o temor de Deus, logo será dominado pelo medo dos homens." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O monge deve viver como se fosse morrer hoje, mas trabalhar como se fosse viver para sempre; assim não desperdiíará nem um dia nem uma hora." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Assim como os peixinhos, se ficam muito tempo em terra seca, morrem, assim também os monásticos, ficando muito tempo com as pessoas do mundo, perdem a tensão do silÉncio." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Devemos nos alegrar com as tentaíões, pois elas são o sinal de que Deus ainda se importa conosco; o que Deus abandona, o diabo também abandona." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Não basta fugir do mundo no corpo; ê preciso fugir do mundo no coraíão. Muitos fugiram com o corpo e continuaram no mundo com os pensamentos." },
  { category: "patristic", author: "Amma Sara", source: "Apotegmas", text: "Luto pela à minha alma como se nunca tivesse comeíado o caminho espiritual; e graías a isso, Deus me dá foría para continuar." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Não sei se serei salvo, mas sei que se fizer o que me compete, Deus fará o que ê de Sua competÉncia." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Vi um irmão que foi muito tentado, e que, ao invês de resistir com as próprias forías, correu para Deus como uma crianía corre para a mãe; e o Senhor o salvou." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A tristeza pelas coisas de Deus gera a compuníão; a tristeza pelas coisas do mundo gera o desespero." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quem procura a honra no serviío de Deus, já recebeu o seu salário; quem busca a honra de Deus, esse aguarda ainda o que lhe será dado." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O espelho da alma ê a compuníão; sem ela, a alma não se vÉ." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "A oraíão não ê fazer muitas palavras; a oraíão verdadeira ê o olhar do coraíão voltado a Deus sem distraíão." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Não me tornei monge pelo jejum, nem pela vigólia, nem pelo trabalho das mãos, mas pelo pensamento constante na presenía de Deus." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "Um irmão perguntou: O que devo fazer para ser salvo? O ancião respondeu: Nunca contentes com o que já fizeste, e não te justifiques diante de Deus; nunca cesses de pedir misericórdia." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "Quem ensina antes de ter aprendido ê como quem constrói uma casa na areia: qualquer chuva a desfaz." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando o irmão cai, não o empurres para baixo; tende-lhe a mão e levanta-o; porque um dia tu mesmo precisarás de uma mão que te levante." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A misericórdia ê a mãe do arrependimento; sem misericórdia, o irmão cado não tem coragem de se levantar." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "A solidão ê boa quando o coraíão está em paz; mas quando o coraíão ê turbulento, a solidão só amplifica a turbulÉncia." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando fores tentado, não te digas: sou forte e vencerei. Dize: Senhor, sou fraco; Tu que ês forte, age em mim." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O demùnio não teme o jejum, nem a vigólia, nem a solidão; só teme a humildade, porque não sabe imitá-la." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O mêdico que não conhece a sua própria doenía, não pode curar a do outro. Assim também o monge que não se conhece a si mesmo, não pode ajudar o irmão." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O coraíão que está cheio de si mesmo não tem espaío para Deus; e o coraíão vazio de si mesmo ê o tabernáculo do Espórito Santo." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Aprendi que a maior vitória não ê sobre o inimigo, mas sobre si mesmo; e que o maior inimigo do monge ê o próprio eu." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Vi todos os laíos do inimigo e disse a Deus: Quem pode escapar destes? E Deus respondeu: O humilde, pois ele passa por baixo de todos os laíos." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A virtude do monge ê conhecida não nas boas horas, mas nas más; não quando a graía está abundante, mas quando está ausente." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O que ê melhor: falar ou calar? Respondeu: Quando a fala ê œtil, fala; quando o silÉncio ê œtil, cala. E os dois sirvam a Deus." },
  { category: "patristic", author: "Abba Silvano", source: "Apotegmas", text: "Se vocÉ tem um bom pensamento e não o coloca em prática, perde-o; se tem um mau pensamento e não o resiste, concretiza-o. Em ambos os casos, a resposta ê a aíão vigilante." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A compaixão pelo próximo não ê fraqueza; ê o coraíão de Cristo que age em mim." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O que ê admirável não ê o monge que realiza grandes milagres; ê aquele que suporta com paz uma palavra dura." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "Quando o próprio discernimento abandona o monge, ele faz coisas grandes por fora mas permanece vazio por dentro." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A fome do corpo ê remediada pelo pão; a fome da alma só pode ser remediada pela presenía de Deus." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "O amor de Deus ê o fundamento e o fim de toda ascese; sem ele, as obras são fárdo sem destino." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "A paz interior não ê ausÉncia de tentaíões; ê a presenía de Deus no meio das tentaíões." },
  { category: "patristic", author: "Abba Arsenio", source: "Apotegmas", text: "Deus não nos abandonou; somos nós que muitas vezes abandonamos a Deus. Mas Ele aguarda pacientemente que voltemos." },
  { category: "patristic", author: "Abba Teodoro de Pherme", source: "Apotegmas", text: "O monge que ê sempre agitado não progride; o monge que ê sempre quieto não serve.  preciso saber quando falar e quando calar, quando agir e quando aguardar." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Quando o coraíão ê puro, a boca fala pureza; quando ê impuro, a boca fala impureza. Por isso o monge vigia primeiro o coraíão." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Deus me mostrou que o monge solitário que pensa mal dos outros nunca obtêm a paz; e o monge no meio dos outros que pensa bem deles, esse mesmo no deserto encontra paz." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Senta-te em tua cela com compuníão; ela te ensinará melhor do que qualquer ancião." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Se o coraíão está vazio de paixões, torna-se claro como espelho; e numa água clara vÉ-se o fundo." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A cela ê o lugar onde Deus e o monge se encontram; quem abandona a cela frequentemente, perde esta oportunidade sagrada." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Há trÉs trabalhos excelentes: temor de Deus, oraíão e compuníão. Estes trÉs, praticados juntos, formam o coraíão do monge." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O que ê o compungimento do coraíão?  a dor que não desesperanía e que nos leva de volta a Deus." },
  { category: "patristic", author: "Abba Arsenio", source: "Apotegmas", text: "Quando eu estava no palácio imperial, não encontrava paz. Quando fugi ao deserto, comecei a encontrar paz. E descobri que a paz não vem do lugar, mas de Deus." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "O comeío de todo bem ê a paz da consciência; sem ela, nem o jejum, nem a vigólia, nem a caridade produzem fruto." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Vi monges que tinham grande ciÉncia e eram como sepulcros caiados; e vi monges simples que eram como tesouros escondidos. Não sejais como os sepulcros." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A primeira coisa que o diabo faz com o monge ê roubar-lhe a compuníão; pois sem compuníão, o monge ê como uma planta sem raózes." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Vi monges que eram muito rigorosos com os outros e muito lenientes consigo mesmos; e vi monges que eram muito rigorosos consigo mesmos e muito misericordiosos com os outros. Os segundos são mais sábios." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Quando o irmão te ofender, não guarda isso na memória; pois a memória das ofensas ê o escorpião que pica sempre que ê tocado." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando a tua mente está em paz, tu podes ajudar o próximo; quando está agitada, tu só aumentas a agitaíão do próximo." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A humildade ê como a terra: todo fruto nasce da terra, e nenhum fruto espiritual nasce sem humildade." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Aprendi que a virtude principal não ê a que se vÉ, mas a que se esconde; não a que ê admirada, mas a que ê exercida em segredo." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que sabe sofrer com alegria já está na porta do Reino; pois o próprio Cristo sofreu com alegria pela nossa salvaíão." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A tentativa de evitar toda tentaíão ê em si uma tentaíão de orgulho; o verdadeiro monge não evita as tentaíões, mas as enfrenta com humildade e oraíão." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O que Deus quer não ê que faíamos coisas grandes, mas que faíamos as coisas pequenas com grande amor." },
  { category: "patristic", author: "Amma Sincletica", source: "Apotegmas", text: "Quando a tentaíão chega pela esquerda, vai para a direita; quando chega pela direita, vai para cima. E acima de tudo, está Deus." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "A órbita do sol nunca para; assim também a mente do monge nunca deve parar de buscar Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O que chamamos de paz espiritual não ê a ausÉncia de combate, mas a presenía de Deus no meio do combate." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Quando Deus nos visita, sentimos como que uma certeza; quando o demonio nos visita, sentimos como que uma dœvida que aumenta progressivamente." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Vence-se a ira não pela resistÉncia, mas pelo silencio; pois a ira ê como fogo que se alimenta do vento das palavras." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando eu lembro dos meus pecados, o amor de Deus me parece ainda maior; e quando lembro do amor de Deus, meus pecados me parecem ainda mais absurdos." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O homem que todos louvam e que não se humilha já comeíou sua queda; o homem que todos criticam e que não perde a paz, esse já comeíou sua salvacão." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando o demùnio não consegue tirar o monge de sua cela, tenta tirar sua paz dentro da cela; se não consegue isso, tenta tirar sua humildade dentro da paz." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que não aprende a orar nos sofrimentos, não aprenderá a rezar nos momentos de consolaíão; pois ê nos sofrimentos que a oraíão se torna real." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Quando souberes que morres, já saberás como viver; pois a memória da morte ê o mestre mais honesto que existe." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Perguntei a Deus: O que ê que os anjos fazem? E me foi respondido: Louvam, servem e obedecem. Eis o programa completo da vida espiritual." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que teme a opinião dos homens não pode agir livremente diante de Deus; e o monge que teme só a Deus, não se preocupa com a opinião dos homens." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "Quando fui ao ancião com trÉs questões e esqueci uma delas no caminho, voltei para buscada; pois nenhuma pergunta ao ancião pode ser abandonada por preguicía." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Vi irmãos que trabalhavam muito e descansavam pouco; e vi irmãos que trabalhavam pouco e descansavam muito. Os primeiros cresciam; os segundos definhavam. O trabalho honesto ê o alimento da alma." },
  { category: "patristic", author: "Abba Serapião de Thmuis", source: "Apotegmas", text: "Não tema a dificuldade da vida espiritual; o que parece difócil no comeío torna-se doce no meio e glorioso no fim." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "O monge que ê tómido na luta espiritual não ê tómido; ê prudente. Mas o que foge da luta ê covaerde. Há uma diferenía entre recuar com discernimento e fugir por medo." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando te sobrevier algum pensamento que te perturbe, não te deixes vencer por ele; mas dize: Este pensamento ê do inimigo, não meu; e ao nome de Jesus, afasta-o." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Vi monges que eram sábios como cobras, e vi monges que eram simples como pombas. Mas os mais sábios eram os que eram ambos: sábios como cobras e simples como pombas." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Não há vócio que não tenha a sua raóz no amor próprio; nem virtude que não tenha a sua raóz no amor a Deus." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando o monge está em paz, os irmãos que chegam saem em paz; quando o monge está agitado, os irmãos que chegam saem ainda mais agitados. A paz interior se comunica." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quem aprende a calar quando está com raiva, aprende tudo; quem não aprende isso, perde tudo o mais que aprendeu." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando fugi do palácio, fugi do ruódo; quando fugi do ruódo, encontrei o silÉncio; quando encontrei o silÉncio, encontrei Deus." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A compuníão ê a lágrima da alma que vÉ Deus e vÉ a si mesma; e ao ver ambos, chora de amor e de vergonha ao mesmo tempo." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "Quando me levantei da obedãencia para a liberdade, percebi que a obediÉncia havia sido a maior liberdade que tive." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Vi irmãos que tinham muita ciÉncia e pouca compuníão; e vi irmãos que tinham pouca ciÉncia e muita compuníão. Os primeiros falavam muito e faziam pouco; os segundos falavam pouco e faziam muito. A compuníão ê mais œtil que a ciÉncia." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O demùnio tem muitos artificioss para destruir o monge, mas o mais eficaz ê este: fazer com que o monge pense que já alcaníou a perfeicão." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A humildade do coraíão ê maior que a humildade do corpo; o corpo pode se prostrar enquanto o coracão se erguea em orgulho." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Aprendi que não ê o lugar que santifica o homem, mas o homem que santifica o lugar; ou antes, Deus que santifica o homem, e o homem santificado santifica o lugar." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O que o Senhor pediu ao jovem rico, pediu a todos nós: Vaó, vende o que tens, dá aos pobres, e vem e segue-me. Não só no sentido material, mas no sentido espiritual: v e vende tua vontade própria." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A verdadeira pob reza não ê não ter nada; ê não querer nada. Pois pode-se não ter nada e estar cheio de desejos; e pode-se ter muito e estar livre de todo desejo." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "O silÉncio do corpo ajuda o silÉncio da alma; e o silÉncio da alma ê o comeío da oraíão." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "O demùnio da acódia diz ao monge: Isso não serve para nada; os outros estão fazendo coisas mais œteis. E se o monge escutar, estará perdido." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que se compar a a outros, já perdeu a batalha interior; pois na vida espiritual, a œnica comparaíão válida ê com quem vocÉ era ontem." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A virtude comeía quando vocÉ decide e acaba quando Deus a consuma; o que está no meio ê a colaboracão entre a liberdade humana e a graía divina." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando o monge ora e não sente nada, não abandone a oraíão; pois o valor da oraíão não está no sentimento, mas na fidelidade." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge fiel ê aquele que ora quando sente e quando não sente, quando está consolado e quando está desolado; pois a fidelidade vale mais que o fervor." },
  { category: "patristic", author: "Abba Silvano", source: "Apotegmas", text: "Vi monges que or avam muito e eram orgulhosos; e vi monges que oravam pouco e eram humildes. Prefiro o monge humilde que ora pouco ao monge orgulhoso que ora muito." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A luta espiritual nunca acaba enquanto vivemos; quem diz que já alcaníou a paz definitiva, provavelmente nunca conheceu a verdadeira luta." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que aprende a aceitar a própria misêria sem desespero e a amar a própria pequenez sem resignaíão, esse já está muito perto de Deus." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Não há nada que Deus não possa perdoar, exceto a negativa do próprio perdão; pois o inferno não ê povoado pelos grandes pecadores, mas pelos que recusaram o arrependimento." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Vi monges que choravam seus pecados sem cessar e depois pecavam de novo sem remorso; e vi monges que pecaram uma vez e não cessaram de chorar durante anos. Os primeiros choravam com a boca; os segundos choravam com o coraíão." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Quando te vier um pensamento de julgamento sobre o irmão, substitui-o imediatamente por um pensamento de misericordia; pois ê a misericordia que Deus quer, e não o julgamento." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Há trÉs sinais de que o demùnio visitou o coraíão: inquietaíão, julgamento dos outros e esquecimento de si mesmo. Há trÉs sinais de que Deus visitou: paz, misericordia e conhecimento de si mesmo." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O demùnio diz ao monge: Vós sois Santos e perfeitos. Deus diz ao monge: Vocs sois pecadores que precissam de misericórdia. Escuta quem diz a verdade." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando o irmão te faz o bem, agradece a Deus por ter enviado o irmão; quando o irmão te faz o mal, agradece a Deus por ter enviado a tua medicina." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O que mais temo não ê o julgamento de Deus, mas o meu próprio julgamento; pois sei que Deus ê misericordioso, mas não sei se eu sou misericordioso comigo mesmo." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A memória da morte ê como a lu de uma lamparina numa noite escura: torna tudo mais claro, mas não ê o sol. O sol ê a presenía de Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando te vier a tentaíão de ensoberbecer-te, lembra dos teus pecados; quando te vier a tentaíão de desesperar por teus pecados, lembra da misericordia de Deus. Os dois remêdios juntos formam o equilóbrio espiritual." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Prefiro ser um ignorante em paz do que um sábio em agitaíão; pois a sabedoria sem paz ê um instrumento que fere quem o carrega." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A maior parte das batalhas espirituais são ganhas não pela foría, mas pela fuga; não pela resistÉncia, mas pela humildade." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando alguém te louva, pensa: Não sou o que ele pensa que sou; quando alguém te critica, pensa: Sou pior do que ele imagina. Ambos os pensamentos te guardam do orgulho." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "A cela ê o lugar de nascimento do monge; pois o monge que não nasce na cela, não nasce de verdade na vida espiritual." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A paciÉncia no sofrimento não ê passividade; ê a foría de quem confia que Deus age mesmo quando nada parece acontecer." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que se compraz em seus sucessos espirituais ê como o agricultor que colhe antes do tempo; o fruto parece maduro, mas por dentro ainda está amargo." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando chegar a hora da morte, o œnico que me poderá ajudar ê o arrependimento que tive na vida; não os jejuns, não as vigólias, não os milagres, mas o arrependimento sincero." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A paz da alma não vem de circunsténcias favoráveis; vem de um coraíão que aprendeu a aceitar todas as circunsténcias como vontade de Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando a alma está limpa, todo o mundo parece belo; quando a alma está suja, todo o mundo parece feio. O que muda não ê o mundo; ê a alma." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O monge que não chora por seus pecados não pode verdadeiramente alegrar-se com a misericórdia de Deus; pois só chora com alegria quem chora primeiro com compuníão." },
  { category: "patristic", author: "Abba Cassiano", source: "Apotegmas", text: "O perigo da vida espiritual não está nos pecados graves, mas nos pequenos deslizes não reconhecidos que acumulam e formam o hábito do mal." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando fugi para o deserto, trouxe comigo tudo o que queria deixar; e descobri que o deserto exterior não purifica o deserto interior." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O que o Espórito Santo faz no coraíão do monge ê como o que o vento faz com o trigo: separa o joio do trigo, e o joio ê levado pelo vento. Mas ê preciso que o trigo permaneía quieto para ser separado." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Vi no deserto monges que tinham os olhos voltados para Deus e monges que tinham os olhos voltados para si mesmos. Os primeiros cresciam em humildade; os segundos cresciam em orgulho." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Quando o monge aprende a rezar em silÉncio, ê porque Deus comeíou a falar; e quando Deus fala, o monge aprende a calar." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A maior prova de amor ao irmão não ê dar o que tens, mas receber o que ele te dá; pois receber com humildade ê mais difócil que dar com generosidade." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "O monge que se glorifica de sua obediÉncia já não ê obediente; a verdadeira obediÉncia não sabe que existe." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando o pensamento mau vier, não o trates como inimigo a combater, mas como enfermo a tratar; pois os pensamentos são fraquezas da alma, não inimigos externos." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O coraíão que está cheio de amor não encontra lugar para o ressentimento; e o coraíão que está cheio de ressentimento não encontra lugar para o amor. São dois estados incompatóveis." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O que ê mais difócil: amar o próximo ou amar Deus? Respondeu: São a mesma coisa. Quem ama Deus ama o próximo; quem não ama o próximo, não ama Deus, por mais que ore." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "O sábio que cala ê duas vezes sábio; o ignorante que cala parece sábio; o sábio que fala demais parece ignorante. Por isso o silÉncio ê sempre proveitoso." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando o monge ê tentado  tristeza, que ore pela alegria dos outros; e quando ê tentado  alegria desordenada, que ore pela dor dos que sofrem. Assim mantêm o equilóbrio." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A leitura das Escrituras alimenta a mente; a oraíão alimenta o coraíão; o jejum alimenta o espórito de combate; mas a humildade sustenta tudo isso junto." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "O monge que faz muitas obras e tem pouca humildade ê como árvore muito florida mas com raózes fracas; na primeira tempestade, cai." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando me perguntam como ê a vida espiritual, respondo:  como aprender a nadar: no comeío parece impossóvel, depois parece difócil, depois se torna natural, e finalmente se torna alegria." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A paz que o mundo oferece ê ausÉncia de conflito exterior; a paz que Cristo oferece ê presenía de Deus no meio do conflito interior." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Vi monges que lutaram durante anos e caóram no fim; e vi monges que caóram no inócio e levantaram-se e chegaram ao fim. A perseveranía final vale mais que o fervor inicial." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que abandona uma cela para ir a outra mais tranquila, descobre que trouxe a intranquilidade consigo; pois a paz não está na cela, mas no coraíão." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "O que ê mais nobre: ter muitas virtudes ou ter humildade? Respondeu: Um só grão de humildade verdadeira vale mais do que todos os campos de virtude sem humildade." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Não se preocupa com a perfeiíão aquele que está sempre comeíando; e comeíar sempre de novo ê a definiíão da vida espiritual." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando Deus me chamou a sair do palácio, não me chamou para a solidão, mas para Si mesmo; a solidão foi apenas o meio, não o fim." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O maior milagre que já vi foi um monge humilhado que não se defendeu; pois a natureza humana, por si mesma, sempre se defende, e não se defender ê obra de Deus." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "A alma que cessa de pedir a Deus, comeía a pedir ao mundo; e o mundo nunca satisfaz o que a alma realmente precisa." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "O arrependimento sincero não fica olhando para o pecado; ele olha para Deus. Não ê o horror do pecado que salva, mas o amor de Deus que atrai." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O que mais me admira no amor de Deus ê que Ele ama os que não merecem ser amados; e se eu entendo isso, comeío a amar os que não merecem ser amados por mim." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Vi um irmão que sempre se acusava e nunca acusava os outros; e vi outro que sempre acusava os outros e nunca a si mesmo. O primeiro crescia; o segundo diminuóa." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que não aprendeu a servir não pode aprender a contemplar; pois a contemplaíão ê o serviío elevado ao seu grau mais alto." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A tentaíão não ê pecado; o consentimento  tentaíão ê pecado. E entre a tentaíão e o consentimento, há um espaío onde mora a liberdade humana e a graía de Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O amor de Deus ê a regra que governa todas as outras regras; sem ele, as regras são gaiola sem pássaro." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "A ascese sem amor ê como corpo sem alma; pode andar, mas não vive." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando eu era jovem, achava que a vida espiritual era conquistar; quando fui velho, aprendi que a vida espiritual ê receber. A graía não ê conquistada; ê recebida." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "O monge que não sabe rezar quando está alegre, não saberá rezar quando estiver triste; pois a oraíão ê hábito, não humor." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quem ê o maior pecador do mundo? Eu mesmo; pois não conheío os pecados dos outros, mas conheío muito bem os meus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A caridade não ê sentimento; ê ato. Não ê gostar do irmão; ê servir ao irmão. E servindo ao irmão, aprende-se a gostar dele." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O que pede a Deus que seja fácil, pede que seja difócil; pois a facilidade amolece a alma. Mas o que pede a Deus que dÉ foría para o difócil, pede o que realmente precisa." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A quietude não ê ausÉncia de pensamentos; ê a presenía de Deus que ordena os pensamentos. O silÉncio verdadeiro está cheio, não vazio." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando o monge está em luta e pede socorro a Deus, e Deus não responde imediatamente, não pense que Deus não ouvia; pense que Deus estava esperando que o pedido se tornasse mais puro." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A virtude que não foi testada não ê virtude; ê potencial de virtude. O teste ê o que transforma o potencial em realidade." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que cuida bem do irmão, cuida melhor de si mesmo do que quando cuida só de si; pois no cuidado do irmão, esquece seus próprios problemas, e Deus cuida dos seus." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando sinto que estou progredindo na vida espiritual, ê quando devo ter mais cuidado; pois o orgulho costuma vir vestido de progresso." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O que transforma o monge não ê a quantidade de oraíão, mas a qualidade da ateníão na oraíão; melhor uma hora com plena ateníão do que dez horas com mente dividida." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A graía de Deus não ê rara; somos nós que somos raros em abrir a mão para recebÉ-la." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "No mundo, aprendi a falar; no deserto, aprendi a calar; e no silÉncio, comecei a ouvir Deus. São trÉs etapas que ninguêm pode pular." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O bem que fazemos em segredo ê como semente enterrada; ninguêm vÉ, mas frutifica. O bem que fazemos em pœblico ê como semente jogada na estrada; todos veem, mas as aves comem." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A oraíão do coraíão não tem palavras; ê um olhar. E quando o coraíão olha para Deus, Deus olha para o coraíão, e neste olhar mœtuo acontece a comunhão." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "O peso da cela ê a maior prova do monge; quem suporta o peso da cela, suporta tudo o mais; quem não suporta a cela, não suportará nada." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Vi muitos que fizeram grandes penitÉncias e não se salvaram por causa do orgulho; e vi muitos que fizeram pequenas penitÉncias e se salvaram pela humildade. A humildade ê o que faz a penitÉncia eficaz." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que procura aprovaíão humana para seus atos espirituais, já se pagou a si mesmo. O que procura só a aprovaíão de Deus, aguarda o seu salário no fim." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Deus me enviou ao deserto não para eu me tornar perfeito, mas para eu aprender que não sou perfeito e precisar de Deus; e descobri que esta necessidade ê a maior das riquezas." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A misericórdia de Deus ê como o mar; não importa quantas vezes nos afogamos, sempre há uma mão que nos puxa de volta  superfócie. Essa mão ê o arrependimento sincero." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "O monge que aprende a sorrir diante da tribulaíão está muito próximo da santidade; pois sorrir na tribulaíão só ê possóvel quando se vÉ Deus por trás dela." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "No silÉncio aprendi mais do que em todos os livros; na solidão aprendi mais do que em todas as conversas. O maior mestre ê Deus, e Ele prefere falar no silÉncio." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A vida espiritual não ê um projeto que se conclui; ê um caminho que nunca acaba. Quem acha que chegou, está comeíando a se enganar." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que ama sua reputaíão mais do que a verdade, perderá ambas no fim; o monge que ama a verdade mais do que a reputaíão, ganhará ambas na eternidade." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A palavra que edifica ê sempre mais curta do que a palavra que destrói; pois construir ê lento e difócil, e destruir ê rápido e fácil." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O amor de Deus ê o œnico amor que não diminui quando ê dividido; ao contrário, quanto mais se divide, mais aumenta. Por isso a caridade ê sinal do amor divino." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O monge maduro não busca experiÉncias espirituais extraordinárias; busca a fidelidade nas coisas ordinárias. A santidade está no ordinário santificado." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "O coraíão que não chora, não ama; e o coraíão que não ama, não vive. A compuníão ê o choro do amor, não o choro do medo." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "O monge que desce de sua cela com mais paz do que subiu, isso ê sinal de Deus; o monge que desce com mais agitaíão, isso ê sinal de que o inimigo trabalhou." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A vida monástica pode ser resumida em duas palavras: rezar e vigiar. Rezar sem vigiar ê sonhar; vigiar sem rezar ê trabalhar em vão." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Quando Deus me pede contas da minha vida, quero mostrar-Lhe não os milagres que fiz, mas as vezes que pedi perdão; não as virtudes que adquiri, mas as quedas das quais me levantei." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "O monge que busca Deus com o coraíão, encontra-O; o monge que busca Deus com a cabeía, discute sobre Ele. Há uma diferenía entre encontrar e discutir." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "A alma que não descansou em Deus descansará nas criaturas; e as criaturas sempre decepcionam no fim. Por isso todo descanso nas criaturas produz eventualmente inquietaíão." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A maior heranía que o monge pode deixar aos discópulos não são as palavras que disse, mas o exemplo que deu; pois as palavras ensinam, mas o exemplo transforma." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que chega ao fim de sua vida sem ter reconhecido sua própria misêria, não chegou ao fim da vida espiritual; chegou ao fim da vida corporal. A vida espiritual comeía com o reconhecimento da própria misêria." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Há um sinal seguro de que Deus está trabalhando na alma: ela comeía a ver sua própria pobreza com mais clareza ao mesmo tempo em que a misericórdia de Deus com mais alegria. Os dois crescem juntos." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "O monge que persevera atê o fim não ê o que nunca caiu; ê o que cada vez que caiu, levantou-se. E cada levantamento ê um ato de fê que fortalece a alma." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A sabedoria espiritual não ê saber muitas coisas; ê fazer bem uma œnica coisa: amar a Deus sobre tudo e ao próximo como a si mesmo. Quem faz isso sabe tudo o que ê necessário." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "No deserto aprendi que Deus não precisa de nossos mêritos para nos amar; Ele nos ama antes de qualquer mêrito. Nossos mêritos são a resposta ao Seu amor, não a condiíão para obtÉ-lo." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O mais belo espetáculo que já vi foi um irmão que foi gravemente ofendido e não respondeu com palavras; apenas baixou a cabeía e rezou. Naquele momento, vi Deus agindo atravês da fraqueza humana." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A vida espiritual tem um œnico comeío e um œnico fim: o amor. Comeía com o amor de Deus por nós e termina com o nosso amor por Deus; e no meio está tudo o mais que chamamos de ascese, oraíão e virtude." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Quando morrer, quero ser encontrado em ato de misericórdia; pois ê pela misericórdia que seremos julgados, e quem foi misericordioso encontrará misericórdia." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O fim da vida espiritual não ê a perfeiíão que eu alcancei, mas a perfeiíão que Deus completará em mim quando eu tiver feito tudo o que me competia fazer com amor e fidelidade." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Vi monges que correram muito e chegaram cansados; e vi monges que caminharam devagar e chegaram descansados. A vida espiritual não premia a velocidade; premia a fidelidade." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "A œltima palavra que aprendi no deserto foi esta: Deus ê amor. E as primeiras palavras que aprendi foram estas: Eu não sou digno de Seu amor. E entre o inócio e o fim, toda a vida espiritual aconteceu." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O monge que chegou ao fim de sua vida com humildade, chegou ao comeío da eternidade; pois na eternidade, a humildade ê o traje de gala dos santos." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Se eu fosse comeíar de novo a vida espiritual, comeíaria com uma œnica resoluíão: nunca julgar o próximo. Desta resoluíão nasceriam todas as virtudes." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "O maior milagre não ê ressuscitar mortos; ê amar o inimigo. E este milagre está ao alcance de todos os que pedem a Deus." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando eu era jovem, pensava que a vida espiritual era subir; quando fui mais velho, aprendi que ê descer. Descer na humildade, no serviío, na compuníão. E descendo, o coraíão sobe atê Deus." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A perfeiíão não está no fim do caminho; está no próprio caminhar com fidelidade. Quem caminha fielmente está perfeito no caminho, mesmo que não tenha chegado ao fim." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando Deus me chamar, não quero apresentar minhas obras; quero apresentar minha compuníão. Pois as obras podem ser orgulho, mas a compuníão ê sempre graía." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A liberdade espiritual não ê fazer o que se quer; ê querer o que se deve. E chegar a querer o que se deve ê o trabalho de toda uma vida." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "O deserto ensinou-me que o silÉncio de Deus não ê ausÉncia; ê plenitude que excede as palavras. Quando Deus cala, ê porque Sua presenía ê maior que qualquer palavra." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que chegoua ao fim da vida sem ter amado verdadeiramente, não chegou ao fim da vida; chegou ao fim de uma possibilidade perdida. A vida só se consuma no amor." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A œltima liíão do deserto ê a primeira liíão do Evangelho: Amai-vos uns aos outros. E toda a vida espiritual não ê mais do que aprender esta liíão, dia após dia, queda após queda, levantamento após levantamento." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando me encontrar com Deus, quero dizer-Lhe: Senhor, fiz pouco; mas o pouco que fiz, fiz com amor. E creio que Ele me responderá: Bem, servo bom e fiel." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O amor de Deus ê a éncora da alma; e a éncora não ê visóvel do exterior do barco, mas ê o que impede o barco de ser arrastado pela correnteza. Assim o amor de Deus, invisóvel para o mundo, ê o que mantêm o monge no rumo da salvaíão." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "A lágrima do arrependimento ê mais preciosa que o ouro; pois o ouro oxida, mas a lágrima sincera nunca se perde." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O silÉncio que nasce do medo ê covardia; o silÉncio que nasce da humildade ê sabedoria; e o silÉncio que nasce do amor ê a oraíão mais profunda que existe." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Aprendi no deserto que Deus não abandona ninguêm que O busca sinceramente; e quem diz que O buscou e não O encontrou, ê porque parou de buscar antes de O encontrar." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que chega ao fim de sua vida e diz: fiz muito, provavelmente fez pouco. O monge que diz: fiz pouco, provavelmente fez muito. Pois o orgulho distorce a visão, e a humildade a clarifica." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "Vi monges que viveram muitos anos no deserto e não se transformaram; e vi monges que viveram poucos anos e foram completamente transformados. O que transforma não ê o tempo, mas a entrega." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Quando chegar a hora de minha morte, quero que minha œltima palavra seja uma aíão de graías; pois a vida toda foi graía, mesmo quando parecia ser provaíão." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O mistêrio da vida espiritual ê este: quanto mais o monge descobre sua misêria, mais descobre a misericórdia de Deus; e quanto mais descobre a misericórdia de Deus, mais cresce em amor. E o amor ê o objetivo de tudo." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A hesychia não ê silÉncio de palavras; ê silÉncio de paixões. E quando as paixões calam, Deus fala." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Se há uma oraíão que resume toda a vida espiritual, ê esta: Senhor, tem misericórdia de mim, pecador. Nela está a humildade, o reconhecimento, a confianía e o amor." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A vida de um monge fiel ê uma longa preparaíão para um œnico momento: o encontro com Deus. E nesse momento, todo o resto perde o valor, exceto o amor com que vivemos." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Aprendi que a santidade não ê a ausÉncia de pecado, mas a abundéncia de misericórdia; e que o santo não ê quem nunca caiu, mas quem nunca parou de se levantar." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "O deserto não ê lugar geográfico; ê estado da alma. E quem encontrou o deserto interior pode estar no meio da cidade e estar mais solitário do que o eremita mais remoto." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando eu era jovem, fugi dos homens para encontrar Deus. Quando fui velho, descobri que Deus estava nos homens que tinha fugido; e que O encontrei no deserto para poder O encontrar neles." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "O amor perfeito não distingue entre amigo e inimigo; vÉ em todos a imagem de Deus, obscurecida pelo pecado, mas presente. E ama essa imagem mesmo quando não consegue amar o pecado que a obscurece." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O fim de toda ascese ê o amor; e o amor ê o comeío de toda ascese. Quem comeía pelo amor, já está no fim; quem comeía pelo esforío sem amor, pode nunca chegar ao fim." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O que mais me deu alegria no deserto não foram os momentos de consolaíão espiritual; foram os momentos de desolaíão onde perseverei por pura fê. Pois nos momentos de consolaíão, a graía carrega; nos momentos de desolaíão, a liberdade escolhe." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A humildade verdadeira não pensa de si nem alto nem baixo; não pensa de si.  o esquecimento de si diante de Deus e do próximo." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando eu era tentado pelo orgulho, lembrava dos meus pecados; quando era tentado pelo desespero, lembrava da misericórdia de Deus. E entre esses dois remêdios, toda a vida espiritual estava contida." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que não tem mestre humano, tem Deus como mestre; mas Deus como mestre ê mais exigente do que qualquer mestre humano, pois exige a entrega total, não apenas a obediÉncia exterior." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Vi um dia um pássaro que cantava no meio de uma tempestade; e pensei: assim deve ser o monge, que louva a Deus no meio de todas as tribulaíões, com a mesma naturalidade com que o pássaro canta." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "A œltima coisa que aprendi no deserto foi que não aprendi nada; e que todo o aprendizado verdadeiro ê o reconhecimento da própria ignoréncia diante de Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O amor de Deus não ê uma emoíão que vem e vai; ê uma decisão que se renova a cada dia. E renová-la nos dias difóceis ê o que a torna verdadeira e profunda." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando eu morrer, não quero que me chamem de sábio, nem de asceta, nem de taumaturgo; quero que me chamem de humilde. Pois ê a œnica coisa que Deus verdadeiramente aprecia." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O deserto me ensinou que quando tudo acaba, Deus comeía; e quando Deus comeía, tudo recomeía." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A minha œltima oraíão será a minha primeira: Senhor, tem misericórdia de mim. Pois no comeío e no fim, só a misericórdia de Deus me salva." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "No dia do julgamento, Deus não me perguntará quantas virtudes tive; me perguntará se amei. E se amei, as virtudes serão consequÉncia; se não amei, as virtudes não valerão nada." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A vida espiritual ê simples: amar a Deus e ao próximo. O que ê complicado não ê a doutrina; são os nossos coraíões que resistem  simplicidade." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O dom mais precioso que recebi no deserto foi aprender a esperar; pois Deus opera em Seu tempo, não no meu, e quem não aprende a esperar, nunca vÉ Deus agir." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando eu encontrar Deus, não serei capaz de dizer nada; apenas me prostrarei. E creio que será nesse silÉncio prostrado que compreenderei, finalmente, tudo o que busquei durante a vida." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A hesychia ê o sinal de que Deus entrou na alma; pois onde Deus entra, a tempestade cessa. Não porque os problemas desaparecem, mas porque Deus ê maior que os problemas." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "A cela ensinou-me que sozinho com Deus não ê solidão; ê a mais plena das companhias. E depois de aprender isso, nunca mais me senti só." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando o monge chega ao fim de sua vida e olha para trás, o que vÉ não são as suas obras, mas as obras de Deus nele. E fica grato não pelo que fez, mas pelo que Deus fez por ele." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "A paz que encontrei no deserto não era a paz que procurei quando entrei; mas era a paz que precisava. Pois Deus sempre nos dá o que precisamos, não o que pedimos." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "No fim de minha vida, o œnico arrependimento que tenho ê não ter amado mais; pois de tudo o mais, Deus me perdoou, mas as oportunidades de amor perdidas não voltam." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A œltima liíão que aprendi foi a primeira que me ensinaram: Deus ê amor. E toda a vida entre o inócio e o fim foi aprender o que isso significa na prática." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Se ao morrer o monge puder dizer honestamente: Senhor, tentei Te amar; então, Deus lhe dirá: Bem, servo fiel; isso me basta." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O deserto não ê um lugar de chegada; ê um lugar de partida. Nele, o monge parte de si mesmo para chegar a Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A œltima liíão da vida espiritual resume todas as outras: Tudo ê graía. E quem entende isso em profundidade, já entendeu o Evangelho." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O monge que morreu bem ê aquele que no fim da vida ainda estava comeíando; pois no Reino de Deus, sempre se comeía de novo, e sempre para melhor." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Quando chegar a minha hora, quero estar em oraíão. Pois a oraíão ê o exercócio da eternidade, e quem morre em oraíão está com o coraíão já aberto para o que vem depois." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Toda a vida espiritual pode ser resumida em duas palavras: confie e espere. Confie em Deus e espere Sua hora; e na hora de Deus, tudo se transforma em graía." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O Dom mais precioso que um monge pode dar ao mundo ê a sua paz; pois o mundo está cheio de agitaíão, e um coraíão em paz ê mais evangelizador do que mil palavras." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "Vi monges que fizeram tudo certo e chegaram ao fim vazios; e vi monges que fizeram muita coisa errada e chegaram ao fim cheios. A diferenía estava no amor: os primeiros amaram a regra; os segundos amaram a Deus." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando meu espórito partir, quero que parta como o pássaro que voa para o sol; e que o sol que me atrai seja o próprio Deus, que ê Luz e Calor e Vida para toda a eternidade." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O œltimo ato de humildade do monge ê aceitar a morte com paz; pois a morte ê o œltimo presente de Deus na terra, que nos entrega definitivamente  Sua misericórdia." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando me perguntam qual ê o segredo da vida espiritual, respondo: não há segredo. Há apenas amor, fidelidade e humildade, repetidos infinitamente, dia após dia, atê o fim." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O monge que chegou ao fim de sua vida com a mesma sede de Deus com que comeíou, esse chegou; pois a sede nunca se sacia na terra, e ê essa insatisfaíão santa que nos abre  plenitude do Cêu." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "Aprendi que não sou nada; aprendi que Deus ê tudo; e aprendi que entre o nada e o tudo, há o amor que une o nada ao tudo. Esta ê a suma da teologia espiritual." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A œltima palavra do deserto ê a primeira palavra do Paraóso: Aleluia. Pois no Paraóso não haverá luta, mas haverá louvor eterno; e quem aprendeu a louvar na luta, já aprendeu a linguagem da eternidade." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Quando eu estiver diante de Deus, não me lembrarei das minhas virtudes; lembrarei apenas de Sua misericórdia. E esta memória será eterna, porque eterna ê a misericórdia de Deus sobre os que O buscaram com humildade." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A vida espiritual não ê um caminho para ser percorrido sozinho; ê uma peregrinaíão em comunidade, onde cada um carrega o peso do outro e Deus carrega o peso de todos." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O irmão que te corrije com amor ê um presente de Deus; o irmão que te elogia sem razão ê um perigo disfarado de presente. Aprenda a distinguir os dois." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando o coraíão está vazio de si mesmo, enche-se de Deus; e quando está cheio de Deus, transborda amor para os irmãos. Este ê o ciclo completo da vida espiritual." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Não ê a foría que vence a tentaíão; ê a humildade. Pois o orgulhoso enfrenta a tentaíão com suas próprias forías e cai; o humilde confessa sua fraqueza e Deus o defende." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "A fuga do mundo não ê covardia; ê sabedoria. Pois o mundo offer a ilusoes de felicidade, e o monge que foge do mundo busca a felicidade real." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O monge que aprendeu a dizer nao a si mesmo, aprendeu a dizer sim a Deus; e essa ê a definiíão completa da ascese cristã." },
  { category: "patristic", author: "Abba João o Anão", source: "Apotegmas", text: "Quando eu comeíei no deserto, pensava que precisava conquistar as virtudes; quando aprendi melhor, percebi que precisava receber as virtudes. A conquista ê nossa; o dom ê de Deus." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O que buscamos no deserto não ê o deserto; ê Deus. E Deus pode ser encontrado no deserto, na cidade, na cela, na comunidade. O que importa não ê o lugar; ê o coraíão." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Quando o monge perseverar atê o fim, não ê porque não teve dificuldades; ê porque teve dificuldades e não desistiu. A perseveranía ê a virtude das virtudes." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "A oracão perfeita não pede nada; ê um fiat, um sim total  vontade de Deus. E quem aprendeu a rezar assim, já aprendeu tudo." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "O humilde não se defende porque sabe que Deus o defende; e Deus defende melhor do que qualquer argumento humano." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "A obra mais importante que o monge realiza nunca ê vista pelos homens; acontece no interior da cela, no silencio do coracão, onde Deus e o monge trabalham juntos." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Aprendi que a perfeicão não ê o destino; ê o processo. E o processo nunca acaba enquanto vivemos, pois sempre há mais amor a aprender." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "O fruto da hesychia ê a claridade: a mente fica clara, o coracão fica limpo, e Deus fica próximo. E quando Deus está próximo, tudo o mais fica no seu devido lugar." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "Vi monges que fizeram muito e foram humildes; e vi monges que fizeram pouco e foram orgulhosos. Os primeiros eram mais avaníados espiritualmente do que os segundos, apesar de parecer o contrário." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "O maior bem que o anciao pode fazer ao discópulo ê mostrar-lhe o caminho; caminhar o caminho, o discópulo deve fazer sozinho com Deus." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando Deus te tirar de dentro da tentaíão sem que tu tenhas feito nada, fica grato; pois Deus s vezes salva não pela nossa foría, mas pela Sua misericordia, para que saibamos de onde vem a nossa salvacão." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "A caridade e a humildade são as duas asas do monge; com uma só, não consegue voar; com as duas, sobe atê Deus." },
  { category: "patristic", author: "Abba João Colobos", source: "Apotegmas", text: "O monge que não perdoa completamente não orou completamente; pois quem pede perdão a Deus e não perdõa o irmão, pede o que não dá." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "A vida espiritual tem uma œnica lei: amar. E todas as outras leis são apenas explicaíões desta lei œnica aplicadas a situacões concretas." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando me aproximei da morte, o que mais senti não foi medo, mas desejo; desejo de ver Aquele que busquei durante toda a vida. E percebi que a vida inteira foi uma preparacão para este encontro." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "O monge que ama a Deus não precisa de regras externas para agir bem; o amor ê a lei interna que governa todas as acões. Mas enquanto o amor não ê perfeito, as regras ajudam." },
  { category: "patristic", author: "Abba Agatão", source: "Apotegmas", text: "A quietude do deserto ensinou-me que Deus não está no ruódo, mas na brisa suave; e que para ouvi-Lo, ê preciso primeiro silenciar tudo o que faz ruódo dentro de nós." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "A santidade não ê superioridade; ê proximidade com Deus. E quanto mais próximo de Deus está o monge, mais se vÉ igual a todos os irmãos, pois vÉ todos na luz de Deus." },
  { category: "patristic", author: "Abba Macário o Grande", source: "Apotegmas", text: "Quando o monge chegar ao Paraóso, não encontrará estranhos; encontrará irmãos. Pois todos os que amaram a Deus são uma familia, mesmo que nunca se tenham visto na terra." },
  { category: "patristic", author: "Abba ArsÉnio", source: "Apotegmas", text: "A œltima liíão do deserto ê que o deserto não era o destino; era o meio. E o fim de todos os meios ê Deus, que está no inócio, no meio e no fim de tudo." },
  { category: "patristic", author: "Abba Poemen", source: "Apotegmas", text: "Vi toda a minha vida espiritual e vi uma coisa: quando eu me esvaziava de mim mesmo, Deus me enchia; e quando eu me enchia de mim mesmo, Deus aguardava. O esvaziamento ê o caminho; o preenchimento ê o dom." },
  { category: "patristic", author: "Abba Sisoes", source: "Apotegmas", text: "Quando tudo acaba, Deus permanece. E quando Deus permanece, nada realmente acabou. Esta ê a esperanía do monge: que alêm da morte, há Vida; e alêm do fim, há Comeío." },
  { category: "patristic", author: "Abba Moisês", source: "Apotegmas", text: "Comecei no deserto com uma pergunta: como me salvo? E terminei com uma resposta: sendo amado por Deus. Pois a salvacão não ê conquista humana; ê don divino recebido com humildade." },
  { category: "patristic", author: "Abba Antùnio", source: "Apotegmas", text: "Se eu tivesse que deixar uma œnica palavra para os que vÉm depois, seria esta: Amai a Deus sobre tudo e ao próximo como a vós mesmos. Neste preceito estão todos os outros. E na prática deste preceito está toda a felicidade possóvel." },
];
